    float sdba= sdBox(rep2-vec3(10.0,1.4,4.0), vec3(0.7,1.2,4.0) );
    float sdbb= sdBox(rep2-vec3(10.0,-1.4,4.0), vec3(0.7,1.2,4.0) );
    float sdbc= sdBox(rep2-vec3(10.0,1.4, -4.5), vec3(0.7,1.2,4.0) );
    float sdbd= sdBox(rep2-vec3(10.0,-1.4,-4.5), vec3(0.7,1.2,4.0) );
    sdb1=differenceSDF(sdb1, sdba);
    sdb1=differenceSDF(sdb1, sdbb);
