#define pi 3.141516

float  sirena(float t, float f_c, float f_m, float beta)
{
    //# t    = time
    //# f_c  = carrier frequency
    //# f_m  = modulation frequency
    //# beta = modulation index
   // return sin(2.0*pi*f_c*t);
    return sin(2.0*pi*f_c*t - beta*sin(2.0*f_m*pi*t)*0.00125*beta*cos(2.0*f_m*pi*t) );



}     

