#define PI 3.14159265359
#define TWO_PI 2.*PI
#define FBM_OCTAVES 4
#define DX 1./iResolution.x
#define DY 1./iResolution.y

//1d pseudo-random for shaders
float random(vec2 p) {
    p = mod(p, 10000.);

 	return fract(sin(dot(p, vec2(12.9898,78.233))) * 43758.5453); 
}

vec3 getPixelated(vec2 p, float size) {
    p = floor(p * size)/size;

    return texture(iChannel1, p).rgb;
}

float charDistance(vec2 p, float char) {
  return texture(
        iChannel0, 
        p/16. + fract( vec2(char, floor(15. - char / 16.)) / 16. )
    ).w;  
}

float getChar(vec2 p, float size) {
    vec3 pix = getPixelated(p, size); 
    float b = dot(pix, vec3(1./3.));

    float char = floor(mix(150., 220., b));
    p = fract(p*size);

	return charDistance(p, char);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0);

    float t = clamp(cos(iTime) * 3. + 3., 0., 1.);  

    float char1 = getChar(uv, 65.);

    vec3 pix = texture(iChannel1, uv).rgb;
    vec3 bw = vec3(dot(pix, vec3(1./3.)));


    float bigChar = charDistance(uv, 82.);

    float d = smoothstep(0.50, 0.49, mix(bigChar, char1, t));

   	col += d * pix; 

    fragColor = vec4(col,1.0);
}
