float hash(vec2 p) {
    p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+342.23);
    return fract(p.x*p.y);

}

mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
     vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
     vec2 uuv = uv;
     uv.x = -abs(uv.x);
     uv *=r(tan(sin(sign(uv.x*uv.y)*iTime/10.))*smoothstep(0.1,0.5,(abs(dot(uv.x,uv.y)*4.))));
     uv *=10.;
     uv *=r(floor(iTime)+pow(fract(iTime),sin(iTime+uv.y)/4.));
     vec2 guv = fract(uv+vec2(iTime,0.))-.5;
     vec2 id = floor(uv+vec2(iTime,0.));  

     if(hash(id) > .5) guv.x*=-1.;
     float d = smoothstep(.0410,.009,abs(length(guv*sign(guv.x+guv.y)-.5)-.5));

     vec3 col = vec3(d);


    //if(guv.x > .49|| guv.y > .49) col.r =1.;
    col = mix(vec3(.01),vec3(.4+sin(uuv.x*100.),.3-abs(uv.y/5.)+hash(id*sin(iTime/500000.)),.5+hash(id*cos(iTime/500000.))),col);
    fragColor = vec4(col,1.0);
}
