#define EPILEPTIC_TRIGGER_WARNING
#ifdef EPILEPTIC_TRIGGER_WARNING
#define speed 1.
#else
#define speed 3.
#endif
#define bpm (mod(iTime,20.))/60.*142.*speed

mat2 r(float a){
    float c=cos(a),s = sin(a);
    return mat2(c,s,-s,c);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 m = iMouse.xy / iResolution.xy;
    float zoom = pow(10.,-m.x*3.);
    vec2 uv = (fragCoord.xy -.5* iResolution.xy) / iResolution.y;


    vec2 c =    vec2(.285,0.01);

    uv= abs(uv)-.12;
     if(uv.x<uv.y) uv.xy=uv.yx;
    uv= abs(uv)-.32;
    if(uv.y<uv.x) uv.xy=uv.yx;
    uv*=r(sin(atan(uv.x,uv.y)*3.+iTime)*.055+pow(floor(bpm),2.));
    uv*=.0040;
    uv +=vec2(-0.0147, .3811);

    float iter = 0.;

    const float max_iter= 150.;

    vec2 ma = vec2(100.);
     vec2 z = uv*2.;
    for(float i=0.;i<max_iter ; i++){
        z  = vec2(pow(z.x,2.) - pow(z.y,2.),2.*z.x*z.y)  + c;
        ma = min(abs(z),ma);
        if(z.x*z.x + z.y*z.y >4.) break;
        iter ++;
    }

    float d = (max_iter- iter)/max_iter;

    d =  pow( length(ma) +  d,2.0)  ;


    vec3 col = vec3(d);
    float qq = speed == 1.0? 2. : (sin(iTime+fract(bpm))*.5+1.5);
    col = mix(vec3(-.5),vec3(.9,.0,.0) + cos(iTime)*.5+1.0,col*qq-fract(bpm));

    fragColor = vec4(col,1.0);
}
