// "Ball Cluster" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// Cluster of attracting balls with friction and other forces (mouseable); based on "More Balls".

#define IN_BOX  0  // in box - same for all shaders
#define S_BIG   1  // big/small system - same for all shaders

#if IN_BOX
float PrBoxDf (vec3 p, vec3 b);
#endif
mat3 QtToRMat (vec4 q);
float SmoothMin (float a, float b, float r);
vec3 HsvToRgb (vec3 c);
float Hashff (float p);
vec4 Loadv4 (int idVar);

#if S_BIG
const int nBall = 512;
#else
const int nBall = 216;
#endif

vec3 vnBall, ltDir, hbSize;
float dstFar;
int idBall;
#if IN_BOX
vec3 rdSign, vnBlk;
int idObj;
#endif
const float pi = 3.14159;

#if IN_BOX

float ObjDf (vec3 p)
{
  vec4 sLen, rsLen;
  vec3 eWid;
  float dMin, d;
  dMin = dstFar;
  sLen = vec4 (hbSize - 0.5, 0.);
  rsLen = vec4 (sLen.xyz * rdSign, 0.);
  eWid = vec3 (0.04);
  d = min (min (
     PrBoxDf (p - rsLen.xww, eWid + sLen.wyz),
     PrBoxDf (p - rsLen.wyw, eWid + sLen.xwz)),
     PrBoxDf (p - rsLen.wwz, eWid + sLen.xyw));
  if (d < dMin) { dMin = d;  idObj = 1; }
  p = abs (p);
  d = min (min (
     PrBoxDf (p - sLen.wyz, eWid + sLen.xww),
     PrBoxDf (p - sLen.xwz, eWid + sLen.wyw)),
     PrBoxDf (p - sLen.xyw, eWid + sLen.wwz));
  if (d < dMin) { dMin = d;  idObj = 2; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 60; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float WallAO (vec3 ro)
{
  vec4 p;
  float ao;
  ao = 1.;
  for (int n = 0; n < nBall; n ++) {
    p = Loadv4 (4 + 4 * n);
    ao = SmoothMin (ao, smoothstep (0.1, 2., length (p.xyz - ro) - p.w), 0.5);
  }
  return 0.6 + 0.4 * ao;
}

#endif

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = 0; n < nBall; n ++) {
    p = Loadv4 (4 + 4 * n);
    u = ro - p.xyz;
    rad = 0.46 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnBall = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
}

#if ! IN_BOX

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 rs, u;
  float b, d, w, dMin, rad, uu, rngSq;
  dMin = dstFar;
  rngSq = rng * rng;
  for (int n = 0; n < nBall; n ++) {
    p = Loadv4 (4 + 4 * n);
    u = ro - p.xyz;
    uu = dot (u, u);
    if (uu < rngSq) {
      rad = 0.46 * p.w;
      b = dot (rd, u);
      w = b * b - uu + rad * rad;
      if (w > 0.) {
        d = - b - sqrt (w);
        if (d > 0. && d < dMin) dMin = d;
      }
    }
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

#endif

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return step (0., sign (a.x) * sign (a.y));
}

#if IN_BOX

float BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = (hbSize - 0.44) / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

float BlkHitSil (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp;
  float dn, df;
  v = ro / rd;
  tp = (hbSize - 0.44) / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  return (df > 0. && dn < df) ? dn : dstFar;
}

#endif

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, col2, vn;
  float dstBall, c, sh, spec, vDotL;
  dstBall = BallHit (ro, rd);
#if IN_BOX
  vec3 w;
  float dstObj;
#endif
#if IN_BOX
  rdSign = sign (rd);
  dstObj = ObjRay (ro, rd);
  if (min (dstBall, dstObj) < dstFar) {
    if (dstBall < dstObj) {
#else
    if (dstBall < dstFar) {
#endif
      ro += rd * dstBall;
      vn = vnBall;
      if (mod (float (idBall), 63.) == 0.) {
        col = vec3 (0.9);
        col2 = vec3 (0.1);
      } else {
        c = Hashff (float (idBall));
        col = HsvToRgb (vec3 (c, 0.9, 1.));
        col2 = HsvToRgb (vec3 (mod (c + 0.4, 1.), 0.9, 1.));
      }
      col = mix (col, col2, BallChqr (idBall, vnBall));
#if ! IN_BOX
      sh = 0.2 + 0.8 * BallHitSh (ro + 0.01 * ltDir, ltDir, 4.);
#else
      sh = 1.;
#endif
      spec = 0.3;
#if IN_BOX
    } else if (dstObj < dstFar) {
      ro += rd * dstObj;
      vn = ObjNf (ro);
      if (idObj == 1) {
        w = smoothstep (0., 0.07, abs (fract (5. * ro / hbSize + 0.5) - 0.5));
        col = vec3 (mix (vec3 (0.4, 0.5, 0.5), vec3 (0.6, 0.6, 0.5),
           dot (abs (vn) * w.yzx * w.zxy, vec3 (1.))));
        col *= WallAO (ro);
      } else if (idObj == 2) col = vec3 (0.6, 0.6, 0.5);
      sh = 1.;
      spec = 0.2;
    }
#endif
    vDotL = max (dot (vn, ltDir), 0.);
    col = col * (0.3 + 0.7 * sh * vDotL * vDotL) +
       step (0.5, sh) * sh * spec * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
#if IN_BOX
    if (BlkHit (ro, rd) < dstFar) col = mix (col,
       vec3 (0.7, 0.7, 0.75) * (0.2 + 0.8 * max (dot (vnBlk, ltDir), 0.)) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vnBlk))), 64.),
       pow (1. - abs (dot (rd, vnBlk)), 4.));
#endif
  }
#if ! IN_BOX
  else col = vec3 (0.9);
#endif
  return pow (clamp (col, 0., 1.), vec3 (0.9));
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 qtVu;
  vec3 col, rd, ro;
  vec2 canvas, uv;
  float zmFac, b;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  hbSize = Loadv4 (0).xyz;
  qtVu = Loadv4 (1);
  vuMat = QtToRMat (qtVu);
  b = length (hbSize);
  dstFar = 10. * b;
#if IN_BOX
  zmFac = 8.;
#else
  zmFac = 10.5;
#endif
  rd = normalize (vec3 (uv, zmFac)) * vuMat;
  ro = vec3 (0., 0., - 6. * b) * vuMat;
  ltDir = normalize (vec3 (0.5, 0.5, -1.)) * vuMat;
#if IN_BOX
  if (BlkHitSil (ro, rd) < dstFar) col = ShowScene (ro, rd);
  else col = vec3 (0., 0., 0.2 + 0.1 * uv.y);
#else
  b = smoothstep (0.12, 0.15, length (max (abs (uv) - vec2 (1.) + 0.15, 0.)));
  if (b < 1.) col = mix (ShowScene (ro, rd), vec3 (0.7), b);
  else col = vec3 (0.7);
#endif
  fragColor = vec4 (col, 1.);
}

#if IN_BOX

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

#endif

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

// "Ball Cluster" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define IN_BOX  0  // in box - same for all shaders
#define S_BIG   1  // big/small system - same for all shaders

vec4 QtMul (vec4 q1, vec4 q2);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
vec4 EulToQt (vec3 e);
vec3 QtToEul (vec4 q);
mat3 LpStepMat (vec3 a);
float SmoothBump (float lo, float hi, float w, float x);
float Hashff (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

#if S_BIG
const int nBall = 512;
#else
const int nBall = 216;
#endif

vec4 qtVu;
vec3 hbSize;
float tCur, nStep;
const float pi = 3.14159;

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm, out float rad)
{
  vec4 p, pmSum;
  vec3 rmN, vmN, wmN, dr, dv, am, wam;
  float fOvlap, fBond, fricN, fricT, fricS, fDamp, fCent, rSep, radN, radAv,
     fc, ft, ms, h, dt, s;
#if IN_BOX
  vec4 drw;
  float fricSW, grav;
#endif
  fOvlap = 1000.;
  fBond = 0.001;
  fricN = 1.;
  fricS = 0.2;
  fricT = 0.5;
  fDamp = 0.05;
  fCent = 1.;
#if IN_BOX
  fricSW = 5.;
  grav = 0.5;
#endif
  p = Loadv4 (4 + 4 * mId);
  rm = p.xyz;
  rad = p.w;
  vm = Loadv4 (4 + 4 * mId + 1).xyz;
  qm = Loadv4 (4 + 4 * mId + 2);
  wm = Loadv4 (4 + 4 * mId + 3).xyz;
  ms = rad * rad * rad;
  pmSum = ms * vec4 (rm, 1.);
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nBall; n ++) {
    p = Loadv4 (4 + 4 * n);
    rmN = p.xyz;
    radN = p.w;
    pmSum += radN * radN * radN * vec4 (rmN, 1.);
    dr = rm - rmN;
    rSep = length (dr);
    radAv = 0.5 * (rad + radN);
    if (n != mId) {
      s = radAv / rSep - 1.;
      vmN = Loadv4 (4 + 4 * n + 1).xyz;
      wmN = Loadv4 (4 + 4 * n + 3).xyz;
      if (s > 0.) {
        fc = fOvlap * s;
        dv = vm - vmN;
        h = dot (dr, dv) / (rSep * rSep);
        fc = max (fc - fricN * h, 0.);
      } else {
        fc = fBond * s;
      }
      am += fc * dr;
      if (s > 0.) {
        dv -= h * dr + cross ((rad * wm + radN * wmN) / (rad + radN), dr);
        ft = min (fricT, fricS * abs (fc) * rSep / max (0.001, length (dv)));
        am -= ft * dv;
        wam += (ft / rSep) * cross (dr, dv);
      }
    }
  }
  pmSum.xyz /= pmSum.w;
#if IN_BOX
  radAv = 0.5 * (rad + 1.);
  drw = vec4 ((hbSize - abs (rm)) * (1. - 2. * step (0., rm)), 0.);
  for (int nf = 0; nf < 3; nf ++) {
    dr = (nf == 1) ? drw.wyw : ((nf == 0) ? drw.xww : drw.wwz);
    rSep = length (dr);
    if (rSep < radAv) {
      fc = fOvlap * (radAv / rSep - 1.);
      dv = vm;
      h = dot (dr, dv) / (rSep * rSep);
      fc = max (fc - fricN * h, 0.);
      am += fc * dr;
      dv -= h * dr + cross (wm, dr);
      ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
      am -= ft * dv;
      wam += (ft / rSep) * cross (dr, dv);
    }
  }
  am -= vec3 (0., grav, 0.) * QtToRMat (qtVu);
  am -= ((mod (float (mId), 16.) == mod (floor (0.5 * tCur), 16.)) ? 5. : 1.) *
     fCent * (rm - pmSum.xyz);
#else
  if (mod (float (mId), 16.) == mod (floor (0.5 * tCur), 16.))
     am -= 6. * fCent * (rm - pmSum.xyz);
  am -= 0.5 * fCent * rm;
#endif
  am -= fDamp * vm;
  dt = 0.01;
  vm += dt * am / ms;
  rm += dt * vm;
#if ! IN_BOX
  rm -= pmSum.xyz;
#endif
  wm += dt * wam / (0.1 * ms * rad);
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm,
   out float rad)
{
  vec3 e;
  float mIdf, nbEdge;
  nbEdge = floor (pow (float (nBall), 1./3.) + 0.1);
  mIdf = float (mId);
  rm = 0.9 * (floor (vec3 (mod (mIdf, nbEdge),
      mod (mIdf, nbEdge * nbEdge) / nbEdge,
      mIdf / (nbEdge * nbEdge))) - 0.5 * (nbEdge - 1.));
  e = vec3 (Hashff (mIdf + tCur), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6));
  vm = 0.1 * normalize (e - 0.5);
  e = normalize (e);
  qm = EulToQt (e);
  wm = 2.5 * e;
  rad = 1. - 0.3 * Hashff (mIdf + 0.1);
}

void OrientVu (inout vec4 qtVu, vec4 mPtr, inout vec4 mPtrP, bool doInit)
{
  vec3 vq1, vq2;
  vec2 dm;
  float mFac;
  if (doInit) {
    qtVu = EulToQt (vec3 (0., 0.5 * pi, 0.));
    mPtrP = vec4 (99., 0., -1., 0.);
  } else {
    if (mPtr.z > 0.) {
      if (mPtrP.x == 99.) mPtrP = mPtr;
      mFac = 1.5;
      dm = - mFac * mPtrP.xy;
      vq1 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      dm = - mFac * mPtr.xy;
      vq2 = vec3 (dm, sqrt (max (1. - dot (dm, dm), 0.)));
      qtVu = normalize (QtMul (vec4 (cross (vq1, vq2), dot (vq1, vq2)), qtVu));
      mPtrP = mPtr;
    } else mPtrP = vec4 (99., 0., -1., 0.);
  }
}

const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, p, qm;
  vec3 rm, vm, wm, euAng;
  vec2 iFrag;
  float tCurP, rad;
  int mId, pxId;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 4) discard;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / iResolution.xy - 0.5;
  if (pxId >= 4) mId = (pxId - 4) / 4;
  else mId = -1;
  doInit = false;
  if (iFrame <= 5) doInit = true;
  if (doInit) {
    nStep = 0.;
    OrientVu (qtVu, mPtr, mPtrP, true);
  } else {
    qtVu = Loadv4 (1);
    mPtrP = Loadv4 (2);
    stDat = Loadv4 (3);
    nStep = stDat.x;
    tCurP = stDat.y;
    ++ nStep;
    euAng = QtToEul (qtVu);
    euAng.z += 0.0001 * pi;
    if (mPtrP.z < 0.) euAng.x += 0.00007 * pi;
    qtVu = EulToQt (euAng);
    OrientVu (qtVu, mPtr, mPtrP, false);
  }
  hbSize = vec3 (0.6 * floor (pow (float (nBall), 1./3.)));
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm, rad);
    else Step (mId, rm, vm, qm, wm, rad);
  }
  if (pxId >= 4) {
    mId = pxId - (4 + 4 * mId);
    p = (mId <= 1) ? ((mId == 0) ? vec4 (rm, rad) : vec4 (vm, 0.)) :
       ((mId == 2) ? qm : vec4 (wm, 0.));
  } else if (pxId == 0) stDat = vec4 (hbSize, 0.);
  else if (pxId == 1) stDat = qtVu;
  else if (pxId == 2) stDat = mPtrP;
  else if (pxId == 3) stDat = vec4 (nStep, tCur, 0., 0.);
  Savev4 (pxId, ((pxId >= 4) ? p : stDat), fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (dot (vec4 (q1.w, - q1.z, q1.yx), q2), dot (vec4 (q1.zw, - q1.x, q1.y), q2),
     dot (vec4 (- q1.y, q1.xwz), q2), dot (vec4 (- q1.xyz, q1.w), q2));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3), c1 * cos (a3)));
}

vec3 QtToEul (vec4 q)
{
  vec3 e;
  float s, t;
  t = dot (q.xy, q.xy);
  s = 2. * sqrt (max (t * (1. - t), 0.));
  e.y = atan (s, 1. - 2. * t);
  if (s != 0.) e.xz = 
     vec2 (atan (q.x * q.z + q.y * q.w, q.x * q.w - q.y * q.z),
           atan (q.x * q.z - q.y * q.w, q.x * q.w + q.y * q.z));
  else e.xz = vec2 (2. * atan (q.z, q.w), 0.);
  return e;
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

