//
// Coronavirus Shader Demo Chain Mail
//
// Mutate this shader and "retransmit" it!
// Be sure to tag "coronavirus"

#define PI 3.1415926538
#define TAU 6.2831853076
#define spokes 5.0

// THESE ARE SOME HELPER FUNCTIONS :-)

// smooth minimum
float smin(float a, float b, float k) {
  float res = exp2(-k * a) + exp2(-k * b);
  return -log2(res) / k;
}

// rotate a in input vec2 around its origin;
vec2 rotate(vec2 v, float a) {
  float s = sin(a);
  float c = cos(a);
  mat2 m = mat2(c, -s, s, c);
  return m * v;
}

// how far a point p is from the line segment from a to b
float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
  return length(pa - ba * h);
}

////// END OF HELPER FUNCTIONS!

// Draws a cool virus!
float virus(vec2 input_pos, vec2 offset, float rotation, float scale) {

  // prepare the coordinates based on the input transformations.
  vec2 pos = rotate(input_pos + offset, rotation) / scale;
  float angle = atan(pos.y, pos.x);

  // this forms the middle blobby part of the virus:
  float sphere = (length(pos) - 0.6); // the base
  float wave = (pow(sin(angle * spokes), 6.) *
                0.31); // goes up and down around the circle
  float body = sphere - wave + (sin(sin(angle * 40.)) * 0.005);

  // These are the lil round nubs of the crown
  float repeatedAngle = floor((angle + .666) * spokes / PI) / spokes * PI;
  repeatedAngle += -.31;

  float boopRadius = 0.9;
  boopRadius += sin(repeatedAngle*8. +iTime *4.) * 0.004;
  float angleSweep = TAU / 100.;

  float startAngle = repeatedAngle - angleSweep;
  float endAngle = repeatedAngle + angleSweep;

  vec2 dotStart = vec2(cos(startAngle), sin(startAngle)) * boopRadius;
  vec2 dotEnd = vec2(cos(endAngle), sin(endAngle)) * boopRadius;
  float crown = sdSegment(pos, dotStart, dotEnd) - 0.1;

  // Combine the nubs and body with smoothing
  return smin(crown, body, 40.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

  vec2 uv = (fragCoord / iResolution.xy - 0.5) *
            2.0; // (0,0) is in the center of the screen

  uv.x *= iResolution.x /
          iResolution.y; // sadly the screen is not square, we must fix uv ratio

  // Background color
  vec3 col = vec3(1.000, 0.891, 0.674);

  // First Virus:
  vec2 offset = vec2(0.2, 0.3 + sin(iTime * 3.) * 0.1);
  float rotation = sin(iTime * 3.) * 0.4 - iTime * 0.2;
  float scale = 0.5 + sin(iTime * 4.) * 0.01;

  float virus1 = virus(uv, offset, rotation, scale);

  // threshhold our SDF gradient to make color regions.

  if (virus1 < 0.0) {

    // green inside
    col *= vec3(0.500, 0.7567, 0.429);
    col *= 1.0 + (uv.y + 0.5) * 0.2;

  } else if (virus1 < 0.04) {
    // pink border
    col = vec3(0.800, 0.567, 0.629);
  }

  // Second Virus:
  offset = vec2(-0.3, -0.33 + sin(iTime * 6.) * 0.05);
  rotation = iTime * 2.;
  scale = 0.4;

  float virus2 = virus(uv, offset, rotation, scale);

  if (virus2 < 0.0) {
    // green inside
    col *= vec3(0.200, 0.467, 0.329);
    col *= 1.0 + (uv.y + 0.5) * 0.2;
  } else if (virus2 < 0.04) {
    // pink border
    col = vec3(0.800, 0.567, 0.629);
  }

  col.yz -= length(pow(uv, vec2(4.0)) * vec2(0.015)); // add the red

  // Output to screen
  fragColor = vec4(col, 1.);
}
