void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float thickness = 0.1;
    float t_usec = (iTime+70.) * .0001;
    vec2 origin = vec2(.4)+t_usec;
    const float pi = 3.14159265;

    // rotation matrix for funzies


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y - 0.5;
    uv.x -= .29;


    //uv = mat2(sin(t_usec), cos(t_usec), sin(t_usec), -cos(t_usec)) * uv;
    float radius = .5*atan(uv.x*uv.y,pow(t_usec,3.))/pow(sin(t_usec),2.4);

    vec3 col = vec3(t_usec+.1, .5*(cos(iTime)*.5+.5),.5); //color 
    vec3 color_scale = length(uv-radius)*col.rgb;

    col.rgb = 0.5*sin(length(uv*radius)+col.rgb);

    col *= length(uv*origin)*color_scale;

    // Time varying pixel color
  	//col += 0.4*cos(iTime)+col;
   	col.g += sin(iTime*.02);
    col.r -= .2*cos(iTime*.02);
    col.g += pow(sin(iTime*.2),2.);


    col.g += atan(sin(iTime),abs(cos(iTime)));

    col.rgb += mix(col.rbg, col.bgr, length(uv-origin)/2.);

   // col.g *= 0.05;

    // Output to screen
    fragColor = vec4(col,1.-length(uv-origin));
}
