////////////////////////////////////////////////////////////////////////////////
//
// Modification by Matthew Arcus of tdhooper's https://www.shadertoy.com/view/wsfGDS
//
// <mouse>: change view
// <up/down>: zoom in/out
// <page up/page down>: change axis of rotation
// '1'-'3': show/hide toruses
// 'a': show/hide coordinate axes
// 'c': centre disc on coordinate axis
// 'g': show R4 coordinate grid
// 't': show plain torus
//
// Uncomment #define of DEBUG to see distance field.
//
// The original showed the x^2+y^2 = 0.5 torus. Here we include the
// x^2+z^2 = 0.5 and x^2+w^2 = 0.5 toruses as well as the coordinate
// axes (actually, the intersections of, for example, the x = y = 0
// plane with the hypersphere).
//
// Also added mouse movement & zoom & fiddled with the raymarching a bit.
//
////////////////////////////////////////////////////////////////////////////////

/*

  Clifford Torus Rotation
  -----------------------

  See also:

  * Animation by Jason Hise https://www.youtube.com/watch?v=1_pzjvVixL0
  * Clifford Torus by mla https://www.shadertoy.com/view/3ss3z4
  * https://en.wikipedia.org/wiki/Clifford_torus
  * http://virtualmathmuseum.org/Surface/clifford_torus/clifford_torus.html
  */

//#define DEBUG

// --------------------------------------------------------
// HG_SDF
// https://www.shadertoy.com/view/Xs3GRB
// --------------------------------------------------------

bool alert = false;

void assert(bool t) {
  if (!t) alert = true;
}

bool eq(float x, float y) {
  return abs(x-y) < 1e-4;
}

#define PI 3.14159265359

const int CHAR_0 = 48;
const int CHAR_A = 65;
const int CHAR_C = 67;
const int CHAR_G = 71;
const int CHAR_T = 84;
const int KEY_PAGE_UP = 33;
const int KEY_PAGE_DOWN = 34;
const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;

bool keypress(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}
int keycount(int key) {
  return int(store(0,key).x);
}

vec2 rotate(vec2 p, float t) {
  return p * cos(t) + vec2(p.y, -p.x) * sin(t);
}

float smax(float a, float b, float r) {
  vec2 u = max(vec2(r + a,r + b), vec2(0));
  return min(-r, max (a, b)) + length(u);
}

// --------------------------------------------------------
// Main SDF
// --------------------------------------------------------

// Inverse stereographic projection of p,
// p4 lies onto the unit 3-sphere centered at 0.
// - Syntopia https://www.shadertoy.com/view/XdfGW4
// k is scale factor
vec4 inverseStereographic(vec3 p, out float k) {
  k = 2.0/(1.0+dot(p,p));
  return vec4(k*p,k-1.0);
}

float fTorus(vec4 p4, float k) {
  // Torus distance
  // Distance from surface x^2 + y^2 = 0.5
  float d = k-length(p4.xy);
  //float d = length(p4.zw)-k;
  return abs(d);
}

bool hitDebugTorus = false;
bool hitDebugPlane = false;

vec4 qmul(vec4 p, vec4 q) {
  return vec4(cross(p.xyz,q.xyz)+p.w*q.xyz+q.w*p.xyz,
              p.w*q.w-dot(p.xyz,q.xyz));
}

vec4 quat;

bool docentre = false;
bvec4 doelement = bvec4(true);

float torus(vec4 p4, vec3 p, float k) {
  float d = fTorus(p4,k);
  if (keypress(CHAR_T)) return d;
  // UV coordinates over the surface, from 0 - 1
  vec2 uv = vec2(atan(p4.y, p4.x),atan(p4.z, p4.w))/PI*0.5+0.5;

  // Recreate domain to be wrapped around the torus surface
  // xy = surface / face, z = depth / distance
  p = vec3(uv, d);

  // Draw some repeated circles
  float n = 16.0;
  float repeat = 1.0/n;
  if (docentre) p.xy += repeat*0.5;
  p.xy = mod(p.xy,repeat);
  p.xy -= repeat*0.5; 

  d = length(p.xy) - repeat * .4;
  d = smax(d, abs(p.z) - .013, .01);
  return d;
}

int type = -1;

float map(vec3 p) {
  float k;
  vec4 p4 = inverseStereographic(p,k);

  // Do a one sided quaternion rotation, otherwise known
  // as a Clifford Translation, which seems appropriate.
  p4 = qmul(p4,quat);

  float d = 1e8;
  type = -1;
  float tradius = 0.707;
  if (doelement[0]) {
    float d0 = d;
    d = min(d,torus(p4.xyzw,p,tradius));
    if (d < d0) type = 0;
  }
  if (doelement[1]) {
    float d0 = d;
    d = min(d,torus(p4.xzyw,p,tradius));
    if (d < d0) type = 1;
  }
  if (doelement[2]) {
    float d0 = d;
    d = min(d,torus(p4.xwyz,p,tradius));
    if (d < d0) type = 2;
  }
  if (doelement[3]) {
    float lwidth = 0.02;
    float d0 = d;
    d = min(d,length(p4.xy)-lwidth);
    d = min(d,length(p4.xz)-lwidth);
    d = min(d,length(p4.xw)-lwidth);
    d = min(d,length(p4.yz)-lwidth);
    d = min(d,length(p4.yw)-lwidth);
    d = min(d,length(p4.zw)-lwidth);
    if (d < d0) type = 3;
  }
  return d/k;
}

float mapDebug(vec3 p) {
  float d = map(p);
#ifndef DEBUG
  return d;
#endif
  float plane = min(abs(p.z), abs(p.y));
  hitDebugPlane = plane < abs(d);
  return hitDebugPlane ? plane : d;
}


// --------------------------------------------------------
// Rendering
// --------------------------------------------------------

vec3 calcNormal(vec3 p) {
  vec3 eps = vec3(.001,0,0);
  vec3 n = vec3(
                map(p + eps.xyy) - map(p - eps.xyy),
                map(p + eps.yxy) - map(p - eps.yxy),
                map(p + eps.yyx) - map(p - eps.yyx)
                );
  return normalize(n);
}

mat3 calcLookAtMatrix(vec3 ro, vec3 ta, vec3 up) {
  vec3 ww = normalize(ta - ro);
  vec3 uu = normalize(cross(ww,up));
  vec3 vv = normalize(cross(uu,ww));
  return mat3(uu, vv, ww);
}

const float ITER = 200.0;
const float MAX_DIST = 20.0;
const float FOG_DIST = 10.0;

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  return p;
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

vec3 axes[] = vec3[] (vec3(1,0,0),  
                      vec3(0,1,0),
                      vec3(0,0,1),
                      vec3(1,1,0),
                      vec3(1,0,1),
                      vec3(0,1,1),
                      vec3(1,1,1));

vec3 getcolor(int type) {
  vec3 color = vec3(0);
  if (type == 3) color = vec3(1,0,0);
  else color = hsv2rgb(float(type)/3.0 + 0.0*iTime,0.2,0.8);
  return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  docentre =  !keypress(CHAR_C);
  doelement[0] = !keypress(CHAR_0+1);
  doelement[1] = !keypress(CHAR_0+2);
  doelement[2] = !keypress(CHAR_0+3);
  doelement[3] = !keypress(CHAR_A);

  vec3 light = normalize(vec3(1,.5,0));
  vec3 camPos = vec3(1.8, 5.5, -5.5);
  vec3 camTar = vec3(.1,0,.1);
  vec3 camUp = vec3(-1,0,-1.5);

  mat3 camMat = calcLookAtMatrix(camPos, camTar, camUp);

  float focalLength = 2.5;
  vec3 p = vec3(2.0*(-iResolution.xy + 2. * fragCoord.xy) / iResolution.y, focalLength);
  p = transform(p);
  vec3 rayDirection = normalize(camMat * p);

  float camera = 4.0;
  camera *= 0.1*float(10+keycount(KEY_DOWN)-keycount(KEY_UP));
  vec3 rayOrigin = vec3(0,0,-camera);
  rayOrigin = transform(rayOrigin);
  rayOrigin = camMat * rayOrigin;

  light = transform(light);
  light = camMat * light;

  vec3 rayPosition = rayOrigin;
  float rayLength = 0.0;

  float distance = 0.0;
  vec3 color = vec3(0);
  int aindex = keycount(KEY_PAGE_UP)-keycount(KEY_PAGE_DOWN);

  aindex %= axes.length();
  if (aindex < 0) aindex += axes.length();
  vec3 axis = normalize(axes[aindex]);
  quat = normalize(vec4(-sin(0.5*iTime)*axis,cos(0.5*iTime)));

  for (float i = 0.; i < ITER; i++) {
    if (distance > 1.0) distance *= 0.5;
    else distance *= 0.8;
    rayLength += distance;
    rayPosition = rayOrigin + rayDirection * rayLength;
    distance = mapDebug(rayPosition);

    if (distance/rayLength < .001) break;
    if (rayLength > MAX_DIST) break;
  }
  float k;
  vec3 normal = calcNormal(rayPosition);
  map(rayPosition); // get type
  color = getcolor(type);
  if (type != 3 && keypress(CHAR_G)) {
    vec4 p4 = inverseStereographic(rayPosition,k);
    p4 = qmul(p4,quat);
    p4 *= 8.0;
    p4 = mod(p4,2.0);
    p4 = min(p4,2.0-p4);
  	float edge = min(min(p4.x,p4.y),min(p4.z,p4.w));
    color *= 0.5+0.5*smoothstep(0.1,0.125,edge);
  }
  color *= dot(light, normal) * .4 + .6;

#if defined DEBUG
  if (hitDebugPlane) {
    float l = PI*length(rayPosition);
    float d = PI*map(rayPosition);
    color = 0.25*vec3(0,1.0+sin(20.0*d),1.0+sin(20.0*l));
  }
#endif
#ifndef DEBUG
  float fog = pow(smoothstep(FOG_DIST, MAX_DIST, rayLength), 1.0);
  color = mix(color, vec3(0), fog);
  color *= mix(1., .025, fog);
#endif

  color = pow(color, vec3(1. / 2.2)); // Gamma
  if (alert) color.x = 1.0;
  fragColor = vec4(color, 1);
}

#define keystate(k,n) ( texelFetch(iChannel0,ivec2(k,n),0) )

void mainImage(out vec4 t, vec2 uv) {
#if __VERSION__ < 300
    t = vec4(0);
#else

    int i = int(uv.x);
    int j = int(uv.y);
    if ( iFrame == 0 ) {
        t = vec4(0);
    } else {
        t = texelFetch(iChannel1,ivec2(i,j),0); // Get current state   
    }  
    if (i == 0) {
        float w1 = keystate(j,2).x;
        if (w1 != t.w) {
            t.w = w1;
            t.x++;
        }
    }     

#endif           
}
