#define time iTime

float wave(float x)
{
    return sin(x * 10.0 + time)* 0.5 * sin(x * 0.5 + time) * .2 + 0.5;
}


//stolen from iq
//https://iquilezles.org/articles/distfunctions2d
float sdRoundedBox( vec2 p, vec2 b, vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}


float drawBox(vec2 uv, vec2 p, vec2 s)
{
    return step(1.0,1.0-sdRoundedBox(uv - p, vec2(.23,.35) * s * 0.7, vec4(0.1) * length(s * 0.7)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.2, .5,1);

    float line = step( .99, 1.0 - (abs(wave(uv.x) - uv.y )));
    col += line * vec3(0, .4, 0);
    float grad = step(uv.y, wave(uv.x)) + line/2.0;
    float b = drawBox(uv, vec2(sin(time * 0.5) * .4 + 0.5, mod(time, 2.0)) , vec2(.23));
    for(int i = 1; i < 101; i++)
    {
        b += drawBox(uv, vec2((sin(float(i))) + sin(time * 5.0 + float(i)) * .09 + 0.5, 
                              mod((time* clamp(((14.0/float(i))),6.0, 20.0) )/float(i), 2.0)) , vec2(.23)) * clamp(1.3-uv.y * 1.25, 0.,1.0);
    }
    b*= 0.12;
    fragColor = vec4( col  +b + grad * vec3(0.1 ,.2, 1),1);
}
