// Moiré's Sun
// my step by step tutorial 7
// by Arthur Stammet, 03.2019

// Inspired by Inigo Quilez https://www.youtube.com/watch?v=0ifChJ0nJfM&t=7s


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set the center to the middle of the screen and make the pixels quadratic
    vec2 p = (fragCoord-.5*iResolution.xy)/iResolution.y; 

    // change values here --------------------------------------------------------------

    float CenterX = 0.0;
	float CenterY = 0.0;

    float radius = 0.25; // radius of the disk ( 0.5 = height of the screen ) 
    float blur = 0.1; // low values for sharper edges

    // Color of the Disk
    float red = 0.8; 	// 0.0 <> 1.0
	float green = 0.5;	// 0.0 <> 1.0
	green += sin(iTime*0.1)*0.2;
    float blue = 0.0; 	// 0.0 <> 1.0

    // Background-Color
    float Bred = 0.0; 	// 0.0 <> 1.0
	float Bgreen = 0.0;	// 0.0 <> 1.0
	float Bblue = 0.4; 	// 0.0 <> 1.0
    Bblue += sin(iTime*0.1)*0.2;

    float ampSpeed = iTime * 0.1;
    float distSpeed = iTime * 0.03;
    float rotSpeed = iTime * 2.;
    float modAmp = 0.5 * (sin(ampSpeed)*0.5); 
    float modFreq = 300. ; 
    float modPhase = rotSpeed; 
    float modDist = 400. + (sin(distSpeed)*400.); 

    // ---------------------------------------------------------------------------------

	vec2 CenterXY = p - vec2 ( CenterX, CenterY );	// center of the disk

	radius += modAmp * cos(atan(CenterXY.x,CenterXY.y) * modFreq + modDist * CenterXY.x + modPhase);

    vec3 disk = vec3 ( red, green, blue ); // disk	
	vec3 back = vec3 ( Bred, Bgreen, Bblue );  // background
    disk *= smoothstep ( radius, radius-blur, length(CenterXY)); // moving disk (animated)
    vec3 col = disk + back;

    fragColor = vec4 ( col, 1.0 );
}
