#define AA  1

mat3 QtToRMat (vec4 q);
float Maxv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

vec3 bSize, vnBlk, fcBlk, ltDir;
vec2 qBlk;
float tCur, dstFar;
const float pi = 3.14159;

const int nBlock = 64;
const vec3 blkSph = vec3 (3.), blkGap = vec3 (0.4);

float GrndHt (vec2 p)
{
  mat2 fqRot;
  vec2 q;
  float h, a;
  fqRot = 2. * mat2 (0.6, -0.8, 0.8, 0.6);
  q = 0.03 * p;
  h = 0.;
  a = 10.;
  for (int j = 0; j < 5; j ++) {
    h += a * Noisefv2 (q);
    a *= 0.5;
    q *= fqRot;
  }
  return h;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 160; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, 0.5 * h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > GrndHt (p.xz)) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy), GrndHt (p.xz + e.yx)), e.x).xzy);
}

float GrndSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 16; j ++) {
    p = ro + d * rd;
    h = p.y - GrndHt (p.xz);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.2, 0.1 * d);
    if (sh < 0.05) break;
  }
  return sh;
}

float BlkHit (vec3 ro, vec3 rd)
{
  mat3 m;
  vec3 rm, rdm, v, tm, tp, u;
  float dMin, dn, df;
  int idBlk;
  dMin = dstFar;
  for (int n = 0; n < nBlock; n ++) {
    rm = Loadv4 (4 * n).xyz;
    m = QtToRMat (Loadv4 (4 * n + 2));
    rdm = rd * m;
    v = ((ro - rm) * m) / rdm;
    tp = bSize / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) {
      dMin = dn;
      fcBlk = - sign (rdm) * step (tm.zxy, tm) * step (tm.yzx, tm);
      idBlk = n;
      u = (v + dn) * rdm;
    }
  }
  if (dMin < dstFar) {
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk));
    vnBlk = QtToRMat (Loadv4 (4 * idBlk + 2)) * fcBlk;
  }
  return dMin;
}

float BlkHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 m;
  vec3 rm, rdm, v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  for (int n = 0; n < nBlock; n ++) {
    rm = Loadv4 (4 * n).xyz;
    m = QtToRMat (Loadv4 (4 * n + 2));
    rdm = rd * m;
    v = ((ro - rm) * m) / rdm;
    tp = bSize / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) dMin = dn;
  }
  return smoothstep (0., rng, dMin);
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 3. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 objCol;
  vec3 col, vn;
  vec2 q;
  float dstBlk, dstGrnd, bSz, sh, w;
  bSize = 0.5 * blkGap * (blkSph - 1.) + 0.4;
  dstBlk = BlkHit (ro, rd);
  dstGrnd = GrndRay (ro, rd);
  if (min (dstBlk, dstGrnd) < dstFar) {
    if (dstBlk < dstGrnd) {
      bSz = bSize.x;
      ro += dstBlk * rd;
      vn = vnBlk;
      if (max (abs (qBlk.x), abs (qBlk.y)) > 0.65) objCol = vec4 (0.7, 0.7, 0.7, 0.3);
      else {
        objCol = vec4 (0.1, 0.1, 0.1, 0.);
        w = -1.;
        if (fcBlk.x == -1. || fcBlk.y == 1.) qBlk = Rot2D (qBlk, 0.25 * pi);
        if (abs (fcBlk.x) == 1.) w = (fcBlk.x > 0.) ? length (qBlk) :
           length (abs (qBlk) - vec2 (0.35 * bSz, 0.));
        else if (abs (fcBlk.y) == 1.) w = (fcBlk.y > 0.) ?
           length (vec2 (mod (qBlk.x + 0.35 * bSz, 0.7 * bSz) - 0.35 * bSz, qBlk.y)) :
           length (abs (qBlk) - 0.4 * bSz);
        else if (abs (fcBlk.z) == 1.) w = (fcBlk.z > 0.) ?
           min (length (abs (qBlk) - 0.4 * bSz), length (qBlk)) :
           length (vec2 (mod (qBlk.x + 0.25 * bSz, 0.5 * bSz) - 0.25 * bSz, abs (qBlk.y) - 0.4 * bSz));
        w -= 0.2 * bSz;
        if (w < 0.) objCol = vec4 (vec3 (1.) * (1. - smoothstep (-0.1, 0.,  w)), -1.);
      }
      sh = 1.;
    } else {
      ro += dstGrnd * rd;
      vn = GrndNf (ro);
      q = abs (mod (0.5 * ro.xz, 1.) - 0.5);
      objCol = vec4 (mix (vec3 (0.2), vec3 (0.8), smoothstep (0.05, 0.1, min (q.x, q.y))), 0.);
      sh = GrndSShadow (ro, ltDir);
    }
    sh = 0.6 + 0.4 * min (sh, BlkHitSh (ro + 0.01 * ltDir, ltDir, 30.));
    if (objCol.a >= 0.) col = objCol.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.) +
       0.1 * max (- dot (vn.xz, normalize (ltDir.xz)), 0.)) +
       objCol.a * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    else col = objCol.rgb;
  } else col = vec3 (0.);
  w = clamp (4. * min (dstBlk, dstGrnd) / dstFar - 3., 0., 1.);
  if (w > 0.) col = mix (col, StarPat (rd, 4.), w);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 col, rd, ro, vd, rMid, rLead;
  vec2 canvas, uv;
  float az, el, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.015 * pi * tCur;
  el = pi * (0.08 + 0.05 * sin (0.021 * pi * tCur));
  if (mPtr.z > 0.) {
    az += pi * mPtr.x;
    el += 0.2 * pi * mPtr.y;
  }
  el = clamp (el, 0.02 * pi, 0.3 * pi);
  rMid = Loadv4 (4 * nBlock).xyz;
  rLead = Loadv4 (4 * nBlock + 1).xyz;
  ro = 0.5 * (rLead + rMid) + 50. * vec3 (cos (el) * cos (az), sin (el), cos (el) * sin (az));
  ro.y = max (ro.y, GrndHt (ro.xz) + 3.);
  vd = normalize (rMid - ro);
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (- vd.y * vd.x, 1. - vd.y * vd.y, - vd.y * vd.z) / sqrt (1. - vd.y * vd.y), vd);
  dstFar = 150.;
  ltDir = normalize (vec3 (1., 2., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 2.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

// "Dice Leaping" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 QtMul (vec4 q1, vec4 q2);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
vec4 EulToQt (vec3 e);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
float Noisefv2 (vec2 p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

vec3 rLead;
float todCur;
const float pi = 3.14159;

const int nBlock = 64, nSiteBk = 27;
const vec3 blkSph = vec3 (3.), blkGap = vec3 (0.4);

float GrndHt (vec2 p)
{
  mat2 fqRot;
  vec2 q;
  float h, a;
  fqRot = 2. * mat2 (0.6, -0.8, 0.8, 0.6);
  q = 0.03 * p;
  h = 0.;
  a = 10.;
  for (int j = 0; j < 5; j ++) {
    h += a * Noisefv2 (q);
    a *= 0.5;
    q *= fqRot;
  }
  return h;
}

vec3 GrndNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy), GrndHt (p.xz + e.yx)), e.x).xzy);
}

vec3 RSite (int sId)
{
  float sIdf;
  sIdf = float (sId);
  return blkGap * (floor (vec3 (mod (sIdf, blkSph.x),
     mod (sIdf, blkSph.x * blkSph.y) / blkSph.x,
     sIdf / (blkSph.x * blkSph.y))) - 0.5 * (blkSph - 1.));
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float vRel, fo, drv;
  const float fOvlap = 1000., fricN = 1., fricT = 1., fricS = 2.;
  fo = fOvlap * (1. / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot, mRotN;
  vec3 rmN, vmN, wmN, dr, dv, rts, rtsN, rms, vms, fc, am, wam, dSp;
  vec2 e;
  float farSep, rSep, grav, fDamp, fAttr, dt;
  e = vec2 (0.1, 0.);
  grav = 5.;
  fDamp = 0.1;
  fAttr = 0.1;
  dt = 0.01;
  rm = Loadv4 (4 * mId).xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  mRot = QtToRMat (qm);
  farSep = length (blkGap * (blkSph - 1.)) + 1.;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = 0; n < nBlock; n ++) {
    rmN = Loadv4 (4 * n).xyz;
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = Loadv4 (4 * n + 1).xyz;
      mRotN = QtToRMat (Loadv4 (4 * n + 2));
      wmN = Loadv4 (4 * n + 3).xyz;
      for (int j = 0; j < nSiteBk; j ++) {
        rts = mRot * RSite (j);
        rms = rm + rts;
        vms = vm + cross (wm, rts);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (int jN = 0; jN < nSiteBk; jN ++) {
          rtsN = mRotN * RSite (jN);
          dr = rms - (rmN + rtsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rtsN));
        }
        am += fc;
        wam += cross (rts, fc);
      }
    }
  }
  for (int j = 0; j < nSiteBk; j ++) {
    rts = mRot * RSite (j);
    dr = rm + rts;
    dr.xz = -0.55 * GrndNf (dr).xz;
    dr.y += 0.55 - GrndHt (rm.xz - dr.xz);
    rSep = length (dr);
    if (rSep < 1.) {
      fc = FcFun (dr, rSep, vm + cross (wm, rts));
      am += fc;
      wam += cross (rts, fc);
    }
  }
  am -= fDamp * vm;
  am.y -= grav;
  am += fAttr * (rLead - rm);
  dSp = blkGap * blkSph;
  wam = mRot * (wam * mRot / (0.25 * (vec3 (dot (dSp, dSp)) - dSp * dSp) + 1.));
  vm += dt * am;
  rm += dt * vm;
  wm += dt * wam;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  float mIdf, nbEdge;
  mIdf = float (mId);
  nbEdge = floor (sqrt (float (nBlock)) + 0.1);
  rm.xz = 4. * (floor (vec2 (mod (mIdf, nbEdge), mIdf / nbEdge)) -
     0.5 * (nbEdge - 1.)) + 64. * Hashff (todCur);
  rm.y = GrndHt (rm.xz) + 0.5 * blkSph.y + 3.;
  vm = 2. * normalize (vec3 (Hashff (mIdf + todCur), Hashff (mIdf + todCur + 0.3),
     Hashff (mIdf + todCur + 0.6)) - 0.5);
  qm = EulToQt (normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6))));
  wm = vec3 (0.);
}

const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, p, qm;
  vec3 rm, vm, wm, rMid;
  vec2 iFrag;
  float nStep;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBlock + 2) discard;
  todCur = iDate.w;
  mId = (pxId < 4 * nBlock) ? pxId / 4 : -1;
  if (iFrame <= 5) {
    doInit = true;
  } else {
    doInit = false;
    stDat = Loadv4 (4 * nBlock);
    nStep = stDat.w;
    ++ nStep;
    rLead = Loadv4 (4 * nBlock + 1).xyz;
    rLead += vec3 (0., 0., 0.05);
    rLead.y = GrndHt (rLead.xz) + 0.1;
    if (mId >= 0) Step (mId, rm, vm, qm, wm);
  }
  if (doInit) {
    nStep = 0.;
    if (mId >= 0) Init (mId, rm, vm, qm, wm);
  }
  if (pxId == 4 * nBlock) {
    rMid = vec3 (0.);
    for (int n = 0; n < nBlock; n ++) rMid += Loadv4 (4 * n).xyz;
    rMid /= float (nBlock);
  }
  if (pxId < 4 * nBlock) {
    kp = 4 * mId;
    if      (pxId == kp + 0) p = vec4 (rm, 0.);
    else if (pxId == kp + 1) p = vec4 (vm, 0.);
    else if (pxId == kp + 2) p = qm;
    else if (pxId == kp + 3) p = vec4 (wm, 0.);
    stDat = p;
  } else {
    kp = 4 * nBlock;
    if      (pxId == kp + 0) stDat = vec4 (rMid, nStep);
    else if (pxId == kp + 1) stDat = vec4 (rLead, 0.);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

const float cHashM = 43758.54;

