// From https://iquilezles.org/articles/distfunctions2d
float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float lineWidth = 0.03;
    float smoothness = 0.005;

    vec2 m = iMouse.xy / iResolution.xy;
   	float aspect = iResolution.x / iResolution.y;

    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= aspect;


    // Two Points
    vec2 a = vec2(m.x * aspect, m.y);
    vec2 b = vec2(0.8 * aspect, 0.8);

    // Make the line
    float l = sdLine(uv, a, b);

    // Get the gradient along the line
    float d = distance( uv,  a) ;
    float d2 = distance(a, b);
    d /= d2;

    // Invert
    d = 1.0 - d;

    // Fatten up the skinny end
    float w = d + 0.3;


    // Smoothstep the sdf, using our gradient to taper 
    float colorOut = 1.0 - smoothstep(lineWidth * w, (lineWidth + smoothness) * w, l);

    // Multiply line with the gradient that is slightly lightened
    colorOut *= d + 0.1;

    fragColor = vec4(colorOut);
}
