//#define func(x,y, freq) (y-cos(x*freq))
#define func(x,y, freq) (g(x,y, freq))

#define pi acos(-1.0)

float g(float x) {

    return sqrt(x*x+1.0)*0.8-1.8;
}

float g(float x, float y, float freq) {

    x *= freq;

    float x1 = abs(mod(x+pi,pi*2.0)-pi);
    float x2 = abs(mod(x   ,pi*2.0)-pi);

    float a = 0.18*freq;

    x1 /= max( y*a+1.0-a,1.0);
    x2 /= max(-y*a+1.0-a,1.0);
    return (mix(-g(x2)-1.0,g(x1)+1.0,clamp(y*0.5+0.5,0.0,1.0)))/max(freq*0.8,1.0)+max(abs(y)-1.0,0.0)*sign(y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 4.0;
    vec2 uv = scale*(fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 muv = scale*(iMouse.xy*2.0-iResolution.xy)/iResolution.y;
    float aa = iResolution.y*0.4;

    float freq = sin(iTime)+1.0;

    float l = func(uv.x, uv.y, freq)/scale;

    vec3 col = vec3(1.0) - sign(l)*vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-2.0*abs(l));
    col *= 0.8 + 0.2*cos(120.0*abs(l));
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,1.0,abs(l)*aa));

    float a = func(muv.x,muv.y, freq);

    col = mix(vec3(0),col,min(1.0,abs(length(muv-uv)-abs(a))*aa/scale));

    // Output to screen
    fragColor = vec4(sqrt(col),1);
}
