// Variant of "GlowLife" by savegor. https://shadertoy.com/view/3tSyWm
// Just adding buffB for background effect, + the last line here to sum it.


float getGlow(vec2 cell, vec2 point) {
    vec2 cell_uv = cell / (iResolution.xy * CELL_SIZE);
    vec4 status = texture(iChannel0, cell_uv);
    float alive = status.b;

    vec2 diff = point - cell;
    float invSqrDist = CELL_SIZE / dot(diff, diff);

    return mix(0.0, invSqrDist, alive);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sumGlow = 0.0;
    float averageArea = CELL_SIZE * GLOW_RAD_FACTOR;

    vec2 cell = floor(fragCoord / CELL_SIZE) * CELL_SIZE;
    cell += CELL_SIZE * 0.5;
    for(float i = -averageArea; i < averageArea + 1.0; i = i + CELL_SIZE) {
        for(float j = -averageArea; j < averageArea + 1.0; j = j + CELL_SIZE) {
            vec2 cell_ij = cell + vec2(i, j);
            sumGlow += getGlow(cell_ij, fragCoord);
        }
    }

    // output color
	float red = sumGlow;
    float green = sumGlow;
    float blue = 2.0 * sumGlow;
    fragColor = vec4(red, green, blue, 1.0);

    // --- Fabrice: added background effect
    fragColor += fract(iTime/6.) > .5 
         ? .2*sin(texture(iChannel1,fragCoord/iResolution.xy))  // variant 1
         : .05*(texture(iChannel1,fragCoord/iResolution.xy));   // variant 2

}

const float CELL_SIZE = 14.0;
const float GLOW_RAD_FACTOR = 8.0;
const int SLOWDOWN = 5;

float noise(vec2 st) {
    return fract(sin(st.x * st.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // just lazy trick to generate first state 
    // sorry
    if(iFrame == 0) { 
    	fragColor = vec4(0.0,0.0,noise(fragCoord) > 0.5,1.0);
    }
    else {
        vec4 mouse = iMouse;
        mouse.xy /= CELL_SIZE;

        vec2 uv = fragCoord / iResolution.xy;
        vec4 status = texture(iChannel0, uv);


        float was_alive = status.b;
        float is_alive = 0.0;
        vec2 diff = abs(fragCoord - mouse.xy);

        // if left mouse button pressed
        // just freeze world and revive selected cells
        if(mouse.z > 0.0) {
            is_alive = was_alive;
            if(diff.x < 0.5 &&  diff.y < 0.5) {
                is_alive = 1.0;    
            }
        }
        else {
            if( iFrame % SLOWDOWN == 0) {
                int living_count = 0;
                for(int i = -1; i < 2; i = i + 1) {
                    for(int j = -1; j < 2; j = j + 1) {

                        float not_center = float((abs(i) + abs(j)) != 0);
                        vec2 uv_ij = (fragCoord + vec2(i, j)) / iResolution.xy;
                        vec4 status_ij = texture(iChannel0, uv_ij);

                        living_count += int(status_ij.z * not_center);
                    }
                }

                float keepAlive = float(living_count == 2 || living_count == 3);
                float revive = float(living_count == 3);

                is_alive = mix(revive, keepAlive, was_alive);
            }
            else {
                is_alive = was_alive;
            }
        }
        fragColor = vec4(0.0,was_alive,is_alive,1.0);
    }
}

// --- Fabrice: alife decorating background triggered by cells
// here: memory by relaxation with space-color deforming effect 

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    int i = int(iTime/6.)%3;                                      // distortion:
    vec2 V =   i == 0 ?  U+ .002*vec2(1,sin(iTime))               //   wind effect
             : i == 1 ? (U-.5)*mat2(cos(.01+vec4(0,11,33,0)))+.5  //   rotation effect
             :          (U-.5)/1.03+.5;                           //   zoom effect

        // memory   space distortion   color-dependant relaxation time 
    O =   texture(iChannel0,V, 1.) * exp(-vec4(1,2,4,1)/100.)
        + texture(iChannel1,U/CELL_SIZE).y; // trigger source
}
