#define PHI (sqrt(5.)*0.5 + 0.5)
#define iTime (iTime / 10.1)



mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
	return mat2(c, -s, s, c);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec2 polarToDecart(vec2 polar) {
    float alpha = polar.x;
    float R = polar.y;
    float x = sin(alpha) * R;
    float y = cos(alpha) * R;
    return vec2(x, y);
}

vec2 decartToPolar(vec2 decart) {
    float alpha = atan(decart.x, decart.y);
    float R = length(decart);
    return vec2(alpha, R);
}

float DE(vec3 z)
{
    // FIXME
    z.x += 5. * sin(iTime);
    z.y += 5. * sin(iTime);
    z.z += 5. * cos(iTime);
    float r;
    int n = 0;
    float Scale = 1.1;
    float Move = 10.;
    while (n < 15) {
        // z.xy *= Rot(0.3 * iTime);
        z.yx *= Rot(iTime * .5);
       if(z.x+z.y<0.) z.xy = -z.yx; // fold 1
       if(z.x+z.z<0.) z.xz = -z.zx; // fold 2
       if(z.y+z.z<0.) z.zy = -z.yz; // fold 3	
        // z-=2.0 * min(0.0, 1.) * n1;
       z = z*Scale - Move*(Scale-1.0);
    //     vec3 n1 = vec3(0.4 * sin(iTime), 0.9, -0.1 * sin(iTime));
    //    float t = dot(z,n1); if (t<0.0) { z-=5.0*t*n1; }
    //     n1 = n1.zyx;
    //    t = dot(z,n1); if (t<0.0) { z-=5.0*t*n1; }
       n++;
    }
    return (length(z)) * pow(Scale, -float(n)) - 0.5;
}

float sdColumn(vec3 p, float width) {
    // float box = sdBox(p, vec3(width, 10., width));
    p *= 5.;
    float fractal = DE(p) / 5.;
    // float column = max(fractal, box);
    // return column;
    // return fractal + abs((p.x + p.z ) / 1.);
    return fractal;
}

// ↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓
vec2 getDist(vec3 p) {
    vec2 ar = decartToPolar(p.xz);
    float a = ar.x, r = ar.y;
    float astep = 2. * PI / 8.; // number of rays
    a = mod(a, astep);
    a -= astep / 2.;
    a = abs(a);
    p.xz = polarToDecart(vec2(a, r));
    // float cyl = length(p.xz - vec2(0, 2)) - 0.2;
    p.xz *= Rot(iTime + p.y * 0.1);
    float column = sdColumn(p, 2.5);
    return vec2(column, RED);
    // return cyl < column? vec2(cyl, BLACK) : vec2(column, IVORY);
//   return vec2(max(DE(p), length(p.xz+vec2(1)) - 0.1), BLUE);
//   return vec2(DE(p) + length(p) - 4., BLUE);
}
// ↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑↑









vec3 rayMarch(vec3 ro, vec3 rd) {
	float d = 0.;
    float info = 0.;
    //float glow = 0.;
    float minAngleToObstacle = 1e10;
    for (int i = 0; i < MAX_STEPS; i++) {
    	vec2 distToClosest = getDist(ro + rd * d);
        minAngleToObstacle = min(minAngleToObstacle, atan(distToClosest.x, d));
        d += abs(distToClosest.x);
        info = distToClosest.y;
        if(abs(distToClosest.x) < EPSILON || d > MAX_DIST) {
        	break;
        }
    }
    return vec3(d, info, minAngleToObstacle);
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(EPSILON, 0.);
    vec3 n = getDist(p).x - vec3(getDist(p - e.xyy).x,
                               getDist(p - e.yxy).x,
                               getDist(p - e.yyx).x);
	return normalize(n);
}



vec3 getRayDirection (vec3 ro, vec2 uv, vec3 lookAt) {
    vec3 rd;
    rd = normalize(vec3(uv - vec2(0, 0.), 1.));
    vec3 lookTo = lookAt - ro;
    float horizAngle = acos(dot(lookTo.xz, rd.xz) / length(lookTo.xz) * length(rd.xz));
    rd.xz *= Rot(horizAngle);
    return rd;
}

vec3 getRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


