uniform float u_time;

float rect(in vec2 st, in vec2 size){
	size = 0.25-size*0.25;
    vec2 uv = step(size,st*(1.0-st));
	return uv.x*uv.y;
}

vec3 DrawBox(int i, vec2 uv, vec4 dim, vec3 col, float blend){
	vec3 colorBox = vec3(0.0);

    if(uv.x>dim[0] && uv.x<dim[1] && uv.y>dim[2] && uv.y<dim[3] ) {
        colorBox = col;
    }

    vec3 blendBar = vec3(177.0/255., 162./255., 97./255.);

     if(uv.x>dim[0] && uv.x<dim[1] && uv.y>-0.2 && uv.y<0.2 ){
     	colorBox = mix(colorBox, blendBar, blend);
	}

    if(uv.x>dim[0]+0.1 && uv.x<dim[1]+0.1 && uv.y>-0.2 && uv.y<0.2 ){

       colorBox = mix(colorBox, blendBar, .7);;
    }

    if(uv.x>-9.0 && uv.x<dim[0]-1.0 && uv.y>-0.2 && uv.y<0.2 ){

       colorBox = mix(colorBox, blendBar, .7);;
    }
    return colorBox; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5; // move center

    vec3 rectColor = vec3(1., abs(sin(iTime))/4.0, 0.0);

    float rectHeight = 0.6;
    float vOffset = 0.1;
    float hOffset = 0.1;
    int numRects = 8;
	vec4 dimArray [8];
    float tempOffset = -0.55 + (sin(iTime)*.1);

    //init rect dimentions array
    for(int i=1; i<numRects-1; i++){
	    //vec4(L, R, T, B)
    	dimArray[i] = vec4 (tempOffset, tempOffset+hOffset, -0.35, 0.35);
    	tempOffset += hOffset*2.0;
    }

    vec3 color = vec3(87.0/255.0, 106.0/255.0, 87.0/255.0);
    float blend = 0.1/0.6;
    float index = 0.0;
    for(int i=0; i<numRects; i++){
    	color += DrawBox(i, uv, dimArray[i], rectColor, blend*index);
    	index +=1.0;   
    }    
    vec3 col = mix(color, vec3(0.5, 0.0, 0.5), abs(sin(iTime))/4.0);
    fragColor = vec4(col,1.0);
}
