mat2 r(float a){return mat2(cos(a),-sin(a),sin(a),cos(a));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy) / iResolution.y;
    uv  = abs(uv);
      vec2 uuv= uv;
      uv=uv*  (length(1.-uv)-.1*uv.x)*2.;
    uv = (2.*uv+.1)-(.5*uv-.1);
      uv *=r(iTime*.1);
    vec2 id = floor(uv*10.);
    uv*=r(length(fract(uuv))-(floor(atan(uuv.x+.05,uuv.y+.05)*3.1415)));    

    uv = fract(uv*10.)-.5;
    uv = abs(uv);
    vec3 col  = vec3(0.,0.,0.0);

     float t =  mix(0.45+(.654211-length(uv)),0.45+(.654211-length(uv))* (sign(sin(.1*iTime-length(id)))*.5 +.5),sin(floor(iTime*.5)+pow(fract(iTime*.5),4.))+1.); 
    uv.x >= t|| uv.y >= t  ? col=vec3(cos(1.66*iTime-length(id)*length(1.-uuv)),sin(-1.66*iTime+(length(id))),length(uv-length(uuv*.1))) : vec3(0.1) ;



    fragColor = vec4(col,1.0);
}
