float rand(vec2 co){
    return fract(sin(dot(co.xy * mod(iTime, 3.14), vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (int(fragCoord.y) == 0) {
		fragColor = vec4(1.0);
	} else {
	  fragColor = vec4(
	  		texelFetch(iChannel0, ivec2(fragCoord) - ivec2(0, 1), 0).rgb
	  	  - vec3(4.0 / 255.0)
	  	  + ((vec3(rand(fragCoord)) - vec3(0.25))
	  	    * vec3(8.0 / 255.0)),
	  	1.0);
	}
}
