#define pi acos(-1.)
//for a global manual override
//cannot define any namespace with dual undersore, -> fatal error
#define VER __VERSION__
//#define VER 100  //webGl1==openGlEs100
//#define VER 300  //webGl2==openGlES300
//there may be some smaller values, labeled "experimental*"
//therefore we only care if (VER <300)
#if VER <300
//outerproduct is glES300 exclusive
mat3 outerProduct(vec3 a,vec3 b){return mat3(a*b.x
                                            ,a*b.y
                                            ,a*b.z);}//i may have swapped a and b wrongly here.
//sign(int) is gles300 exclusive
int sign(int a){return int(sign(float(a)));}
//abs(int) is gles300 exclusive
int abs(int a){return (a)*sign(a);}
//texelFetch() uses gles300 exclusive ivec2, substituting with texture(,(vec2()*.5)/iResolution.xy)
vec4 tf(sampler2D a,vec2 b,vec3 r){return texture(a,(b+.5)/r.xy) ;}
#else
vec4 tf(sampler2D a,vec2 b,vec3 r){return texelFetch(a,ivec2(b),0) ;}
#endif
mat4 rotationAxisAngle(vec3 v,float r//axis,angle
){float s=sin(r),c=cos(r);return 
  mat4(mat3(c)
      +outerProduct(v,v)*(1.-c)
      +mat3(0,-v.z,v.yz,0,-v.xy,v.x,0)*s)
 +mat4(1)-mat4(mat3(1));}
mat4 translate(vec3 t){mat4 m=mat4(1);m[3]=vec4(t.xyz,1);return m;}

