 ;}//https://stackoverflow.com/questions/9600801/evenly-distributing-n-points-on-a-sphere/44164075#44164075
vec3 uniformVector(int seed
){const float points=pow(2.,22.)  //[machineEpsilon]=pow(2.,-23)
 ;float f=fhash1(float(seed))
 ;return fibSphereKthOfNpoints(mod(f,points),points);}

//the last remaining hash2() subroutine call is deconstructzed in here:
vec3 cosineDirection(int seed, in vec3 nor
){seed=seed*7+11
 ;int n=int(seed)    
 ;float f=float(n)*pow(2.,13.)
 ;//n = int(f)^n //original integer hash used a mask, but thats slow in glES100, and its fine without mask.
 ;n=n*(n*n*15731+789221)+1376312589
 ;int nx=n*n
 ;int ny=n*n*16807
 ;nx=abs(nx)
 ;ny=abs(nx)
 ;vec2 r=vec2(nx,ny)
  ;r=r/float(0x7fffffff)
    //;vec2 r = hash2( seed);
    // by fizzer: http://www.amietia.com/lambertnotangent.html
    ;float a = 6.2831853 * r.y;
    float u = 2.0*r.x - 1.0;
    return normalize( nor + vec3(sqrt(1.0-u*u) * vec2(cos(a), sin(a)), u) );
}

