Main // v+=f p+=v+f
{
    U = floor(U);
    if (U.x>N||U.y>=2.) {discard;return;}

    vec3 pos = A(vec2(U.x,0)).xyz,
         vel = A(vec2(U.x,1)).xyz,
         frc = B(vec2(U.x,0)).xyz;
    vel += frc;
    pos += frc + vel*sqrt(1./(1.+dot(vel,vel)));
    if (length(vel)>.1) vel = .1*normalize(vel);

    if (pos.x<-1.) {pos.x=-1.; vel.x*=-.5;}
    if (pos.y<-1.) {pos.y=-1.; vel.y*=-.5;}
    if (pos.z<-1.) {pos.z=-1.; vel.z*=-.5;}
    if (pos.x>1.) {pos.x=1.; vel.x*=-.5;}
    if (pos.y>1.) {pos.y=1.; vel.y*=-.5;}
    if (pos.z>1.) {pos.z=1.; vel.z*=-.5;}

    if (iFrame < 1) {
    	pos = vec3(sin(U.x),cos(U.x),sin(sqrt(5.)*U.x));
        vel = vec3(0);
    }


    if (U.y<1.) 
    	Q.xyz = pos;
    else if (U.y < 2.) 
    	Q.xyz = vel;

}

Main // calculate forces
{
    U = floor(U);
    vec3 f = vec3(0);
    vec3 pos = A(vec2(U.x,0)).xyz;
	if (U.x>N||U.y>=1.) {discard;return;}
    for (float i = 0.; i < N; i++) {
    	vec4 a = A(vec2(i,0));
        vec3 r = a.xyz-pos.xyz;
        float l = length(r);
        float ch = charge(U.x)*charge(i);
        if (l>1e-2) 
        f += (.01-ch)*r/l/l-.01*(1.5+ch)*r/l/l/l/l/l;
    }
    vec3 r = (pos-vec3(0));
    float l = length(r);
    if (l>1e-1) 
        f += -r/length(r);
    f = 1e-4*f;
    if (length(f)>1.) f = normalize(f);
    Q.xyz = f;
}

