vec4 triplanar (vec3 p, vec3 n, out vec3 norm) {
    p = (p-0.5)* .5*iChannelResolution[3].x;
	vec4
        a = D(p.xy+0.5*iChannelResolution[3].xy),
        b = D(p.yz+0.5*iChannelResolution[3].xy),
        c = D(p.zx+0.5*iChannelResolution[3].xy);
    vec3
        an = B(p.xy+0.5*iChannelResolution[3].xy).xyz,
        bn = B(p.yz+0.5*iChannelResolution[3].xy).yzx,
        cn = B(p.zx+0.5*iChannelResolution[3].xy).zxy;
    norm = (an*n.z+bn*n.x+cn*n.y);
    return a*n.z*n.z+b*n.x*n.x+c*n.y*n.y;
}
vec2 sphere (vec3 p, vec3 d, float r){
    float C = dot(p,p)-r*r,
        B = 2.*dot(p,d),
        det = B*B-4.*C;
    if (det < 0.) return vec2(-1,0);
    float i = 0.5*(-B-sqrt(det));
    if (i>0.) return vec2(i,1); 
    else return vec2(0.5*(-B+sqrt(det)),-1);
}
