#define AA      0

const float fltMax = 1000000.;
const float fltMin = -1000000.;


bool convexIntersect( in vec3 ro, in vec3 rd, out vec2 oDis, out vec3 oNor)
{
    oDis = vec2(fltMin, fltMax);
    for(int i = 0 ;i < numPlanes; i++)
    {
        vec4 plane = planes[i];
        float t = -(plane.w + dot(plane.xyz, ro)) / dot(plane.xyz, rd);
        if(dot(plane.xyz, rd) < 0.) // enter
        {
            if(t > oDis.x)
            {
                oDis.x = t;
                oNor = plane.xyz;
            }
        }
        else  // exit
        {
            oDis.y = min(oDis.y, t);
        }
    }
    if(oDis.x < oDis.y)
    {
        return true;
    }
    return false;
}

vec3 render(in vec3 ro,in vec3 rd)
{
    vec3 col;
    vec3  n;
    vec2  d;
    if(convexIntersect(ro, rd, d, n))
    {
        col = n * 0.5 + 0.5;
    }
    else
    {
        col = vec3(0.2);
    }

    return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 tot = vec3(0.0);      
#if AA
	vec2 rook[4];
    rook[0] = vec2( 1./8., 3./8.);
    rook[1] = vec2( 3./8.,-1./8.);
    rook[2] = vec2(-1./8.,-3./8.);
    rook[3] = vec2(-3./8., 1./8.);
    for( int n=0; n<4; ++n )
    {
        // pixel coordinates
        vec2 o = rook[n];
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else //AA
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif //AA
        // camera        
        float theta	= radians(360.) * (iMouse.x/iResolution.x-0.5) + iTime*.2;
        float phi	= radians(110.) * (iMouse.y/iResolution.y-0.5) - radians(90.);
        vec3 ro = 3. * vec3( sin(phi) * cos(theta), cos(phi), sin(phi) * sin(theta));
        vec3 ta = vec3( 0 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta );
        vec3 rd =  ca*normalize(vec3(p,1.5));        
        vec3 col = render(ro ,rd);  
        tot += col;            
#if AA
    }
    tot /= 4.;
#endif
    fragColor = vec4( tot, 1.0 );
}
