// Intersect the o->d ray with a window of the waveform DOWNSAMPLE_FACTOR+1 samples wide, accounting
// for scaling and rotation. The "Buffer B" texture stores min/max real/imaginary values for this
// window
bool bbox_intersect(vec3 o, vec3 d, int index, float rotation, float tex_scale, float scale_out) {
    float u = (float(index) + .5) / tex_scale;
    vec2 uv = vec2(u, rotation);
    // One texture sample provides the min/max y/z coordinates of a DOWNSAMPLE_FACTOR-wide
    // window of segments. Also add a WAVE_SIZE-width fudge factor to the window
    vec4 s = texture(iChannel1, uv, 0.);
    float x1 = (float(index) * scale_out) / tex_scale - WAVE_SIZE,
          x2 = (float(index+DOWNSAMPLE_FACTOR+1) * scale_out) / tex_scale + WAVE_SIZE;

    // Get the two points of intersection of the ray with the
    // x=[index,index+DOWNSAMPLE_FACTOR+1] planes
    float t1 = (x1 - o.x) / d.x;
    vec2 p1 = o.yz + t1 * d.yz;
    float t2 = (x2 - o.x) / d.x;
    vec2 p2 = o.yz + t2 * d.yz;

    // The coordinates here are confusing: p1.xy is logically the yz coordinates
    float min_y = min(p1.x, p2.x), max_y = max(p1.x, p2.x),
          min_z = min(p1.y, p2.y), max_z = max(p1.y, p2.y);

    // Test bounding box intersection. Note that the sample is (min_y, max_y, min_z, max_z)
    return (min_y > s.y || max_y < s.x || min_z > s.w || max_z < s.z);
}

// Sample the waveform texture based on the waveform phase and the rotation
void get_segment(int index, float rotation, out vec3 a, out vec3 b,
        float tex_scale, float scale_out) {
    float u = (float(index) + .5) / tex_scale;
    vec2 uv = vec2(u, rotation);
    // One texture sample provides the y/z coordinates of two adjacent samples
    vec4 s = texture(iChannel0, uv, 0.);
    a = vec3(float(index)   * scale_out / tex_scale, s.xy);
    b = vec3(float(index+1) * scale_out / tex_scale, s.zw);
}

// Test intersection of a ray o->d with a cylinder around the x axis of a given
// radius, returning the x coordinates of the two intersection points if it does
bool cyl_intersect(vec3 o1, vec3 d1, float radius, out float x1, out float x2) {
    const vec3 o2 = vec3(0, 0, 0);
    const vec3 d2 = vec3(1, 0, 0);
    vec3 n = cross(d1, d2);

    // Quick check that the ray gets closer than the radius
    float len = abs(dot(normalize(n), o2 - o1));
    if (len >= radius)
        return false;

    // Get the nearest point on the ray to the x axis
    vec3 n2 = cross(d2, n);
    float t1 = dot(o2 - o1, n2) / dot(d1, n2);
    vec3 c1 = o1 + t1 * d1;

    // Get the +/-z offsets of where the intersection points would be if the
    // ray is parallel to the z axis. The ray should always be closer than
    // the radius here, modulo precision issues...?
    float z = sqrt(radius*radius - len*len);

    // Use the x/z components of the ray's direction to see how far along the
    // x axis the intersection points are. We only need the x component, so
    // we don't care about the angle of the ray's direction about the x axis
    float xdelta = d1.x * z / d1.z;
    x1 = c1.x + xdelta;
    x2 = c1.x - xdelta;
    return true;
}

// Find distance and nearest intersection point from the ray o->d to the
// line or segment a->b (use_clamp=true <=> segment)
float line_dist(vec3 o, vec3 d, vec3 a, vec3 b, bool use_clamp, out vec3 p) {
    vec3 d2 = b - a;
    vec3 n = cross(d, d2);
    vec3 n1 = cross(d, n);
    // Get the parameter t of the nearest point on the line a+t*b
    float t2 = dot(o - a, n1) / dot(d2, n1);
    // If this is just the segment ab, clamp to the endpoints
    if (use_clamp)
        t2 = clamp(t2, 0., 1.);
    p = a + d2 * t2;
    vec3 po = p - o;
    float h = dot(po, d) / dot(d, d);
    return length(po - d*h);
}

float line_distance(vec3 o, vec3 d, vec3 a, vec3 d2, out vec3 p) {
    return line_dist(o, d, a, a + d2, false, p);
}

float segment_distance(vec3 o, vec3 d, vec3 a, vec3 b, out vec3 p) {
    return line_dist(o, d, a, b, true, p);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Number of horizontal samples in the rendered waveform texture
    float TEX_SCALE = iChannelResolution[0].x;
    // Wavelength scale
    float SCALE = .5;
    // Maximum magnitude of complex wave
    const float BOUND = wavetable[0].x * GAIN + WAVE_SIZE;
    // Fudge factor for waveform texture lookup to compensate for waveform thickness
    const int FUDGE = 10;

    // Compute screen coordinates based on the max dimension, to keep coordinates
    // proportional/square
    float res = max(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5 * iResolution.xy) / res;

    // Rotate based on time
    float rotation = mod(ROTATION_RATE * iTime + .25, 1.);

    // Get camera rotation about the x and y axes from mouse position, in
    // the form of (z, y)- and (z, x)-coordinate complex numbers
    vec2 mouse = iMouse.xy / iResolution.xy - .5;
    // "Demo mode" -> move camera if no mouse movement
    if (iMouse.x == 0. && iMouse.y == 0.) {
        float tf = clamp((iTime - 3.) / 5., 0., 1.);
        mouse = vec2(mix(0., .5, tf),
                mix(0., .015, tf));
    }
    vec2 cang = mouse * 3.;
    vec2 czy = vec2(cos(cang.y), sin(cang.y));
    vec2 czx = vec2(cos(cang.x), sin(cang.x));
    // Rotate the screen coordinates by the camera angles, and zoom out
    vec2 rzy = cmul(vec2(1, uv.y), czy);
    vec2 rzx = cmul(vec2(rzy.x, uv.x), czx);
    vec3 ro = CAMERA_DISTANCE * vec3(rzx.y, rzy.y, rzx.x);
    // Get the camera direction by rotating the y and x axis rotations, negating,
    // and adding a bit of perspective correction.
    vec2 crzx = cmul(vec2(czy.x, 0), czx);
    vec3 cam = vec3(crzx.y, czy.y, crzx.x);
    vec3 rd = PERSPECTIVE_FACTOR * ro - cam;

    // Find the min/max x/y values of interest by intersecting the ray with
    // the BOUND-radius cylinder around the x axis. BOUND is the maximum magnitude
    // of the waveform (always the first sample's real part for us).
    // The min/max x values dictate how many waveform samples we look at.
    float cx1, cx2;
    float min_dist = WAVE_SIZE, min_depth = 1e10;
    float z = -100.;
    if (cyl_intersect(ro, rd, BOUND, cx1, cx2)) {
        float x1 = min(cx1, cx2), x2 = max(cx1, cx2);

        // Translate x values to min/max integer indices. As a small optimization,
        // we convert based on the texture size (Shadertoy uses the viewport resolution
        // for render buffers, so we can't just use the waveform size). This means we
        // only sample each texel once in the loop, and we can use "nearest" filtering.
        // We also add a fudge factor due to the thickness of the waveform lines, and
        // clamp the max number of samples a pixel will look at so the shader doesn't
        // completely die if the camera goes too far sideways.
        int idx_lo = int(x1 * TEX_SCALE / SCALE) - FUDGE,
            idx_hi = min(int(x2 * TEX_SCALE / SCALE) + FUDGE, idx_lo + MAX_SAMPLES);

        // For each x value with a different texel, sample the texture. We get two
        // adjacent waveform samples from each texture sample, and use these as endpoints
        // of a 3D line segment that we check distance from the ray from
        for (int x = idx_lo; x <= idx_hi; x++) {
            // First, check if the view ray intersects the bounding box of the wave for
            // the next DOWNSAMPLE_FACTOR segments
            if (bbox_intersect(ro, rd, x, rotation, TEX_SCALE, SCALE)) {
                x += DOWNSAMPLE_FACTOR;
                continue;
            }
            vec3 a, b;
            get_segment(x, rotation, a, b, TEX_SCALE, SCALE);

            vec3 p;
            float d = segment_distance(ro, rd, a, b, p);
            float depth = ro.z - p.z;
            // Separate min-distance and Z testing
            if (d < WAVE_SIZE && depth < min_depth) {
                min_depth = depth;
                z = p.z;
            }
            if (d < min_dist)
                min_dist = d;
        }
    }

    // Compute nearest grid line
    float grid_dist = 1000.;
    const int GRID = 1;
    const int N_GRID_LINES = 40;
    float gz = -1000.;
    for (int i = -N_GRID_LINES; i < N_GRID_LINES; i++) {
        float f = float(i) / float(GRID);
        // Test grid lines in three dimensions
        vec3 start[] = vec3[](
                vec3(f, 0, 0), vec3(0, f, 0), vec3(f, 0, 0));
        vec3 dir[] = vec3[](
                vec3(0, 1, 0), vec3(1, 0, 0), vec3(0, 0, 1));

        for (int n = 0; n < 3; n++) {
            vec3 p;
            float d = line_distance(ro, rd, start[n], dir[n], p);
            if (d < grid_dist)
                grid_dist = d;
            if (d < GRID_SIZE && p.z < 4. && p.z > gz)
                gz = p.z;
        }
    }

    // Compute final pixel color. This uses some hacky code for basic anti-aliasing
    // and depth testing. Ideally, we'd compute alpha/color for each grid/waveform
    // point that touch each ray, and do full blending/depth testing, but that's
    // complicated and probably quite slow
    const vec4 grey = vec4(1, 1, 1, 1) * .9;
    fragColor = vec4(0, 0, 0, 1);
    // Waveform color, based on the z value found and distance from the center of
    // the 3d waveform path--we roll off for basic antialiasing
    float c = z/BOUND + .5;
    vec4 wcolor = vec4(c, c-.5, 1.-c, 1) * smoothstep(1., 0., min_dist*140. - .9);
    if (grid_dist < GRID_SIZE && gz > -50. && gz < 4.) {
        // Break ties (where both grid/waveform are visible) based on the closest
        if (min_dist < WAVE_SIZE && z > gz)
            fragColor = wcolor;
        else {
            float a = clamp(smoothstep(1., 0., grid_dist*250. - .1), 0., 1.);
            fragColor = grey * a * 1. / (4. - gz/4.);
        }
    } else if (min_dist < WAVE_SIZE)
        fragColor = wcolor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv1 = fragCoord / iResolution.xy;
    vec2 uv2 = (fragCoord + vec2(1, 0)) / iResolution.xy;

    float t = uv1.y * PI * 2.;
    vec2 rotate = vec2(cos(t), sin(t));

    // We have four channels to store data in, so compute two adjacent samples and
    // store both as one segment
    vec2 s1 = get_sample_rot(uv1.x, rotate),
         s2 = get_sample_rot(uv2.x, rotate);

    fragColor = vec4(s1, s2);
}

// Buffer B stores min/max values of the real/imaginary components of samples in a
// range of the wavetable (the window controlled by DOWNSAMPLE_FACTOR)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv1 = fragCoord / iResolution.xy;

    float t = uv1.y * PI * 2.;
    vec2 rotate = vec2(cos(t), sin(t));

    // Get the min/max values of the next DOWNSAMPLE_FACTOR+1 samples. The +1 is
    // because we store segment endpoints, and X segments have X+1 endpoints.
    // Note that sample xy coordinates correspond to logical yz coordinates
    vec2 s1 = get_sample_rot(uv1.x, rotate);
    float min_y = s1.x, max_y = s1.x, min_z = s1.y, max_z = s1.y;
    for (int i = 1; i < DOWNSAMPLE_FACTOR+2; i++) {
    	vec2 uv2 = (fragCoord + vec2(i, 0)) / iResolution.xy;
    	vec2 s2 = get_sample_rot(uv2.x, rotate);

        min_y = min(min_y, s2.x);
        max_y = max(max_y, s2.x);
        min_z = min(min_z, s2.y);
        max_z = max(max_z, s2.y);
    }

    // Store the extents plus a WAVE_SIZE fudge factor
    const float M = 1.*WAVE_SIZE;
    fragColor = vec4(min_y-M, max_y+M, min_z-M, max_z+M);
}
