mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

vec4 r4(float n) {
	#define R4P 1.1673039782614187
    return fract(.5+vec4(1./R4P,1./R4P/R4P,1./R4P/R4P/R4P,1./R4P/R4P/R4P/R4P)*n);
}
vec4 hash(vec4 a) {
    a = mod(abs(a),8273.97234);
    #define R4S(sw) floor(fract(.352347+dot(a,vec4(.001,.1,10.,100.).sw*2.23454))*20000.)
    return r4(R4S(xyzw)+R4S(yzwx)+R4S(wxyz)+R4S(zwxy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = mod(iTime,15.);
    vec2 uv = (fragCoord*2.-iResolution.xy)*mix(1.,.125,pow(time/15.,2.))/iResolution.y;

	float d = 1e8;
	vec2 p = uv;
	for (float s = 1.; s < 17.; s++) {
		d = min(d,length(max(abs(p)-vec2(.02,.02),0.)));
		p = abs(p-(.01+time*.01))*r2(s*.004*time);
	}
	d = mix(d,length(max(abs(uv-.001)-.02,0.))*4.,pow(clamp((time-10.)*.201,0.,1.),8.));
	d *= iResolution.y*.5;
	fragColor = vec4(d,d,d,1);
}
