// golfing https://shadertoy.com/view/MlsXDr ( 277 chars )

// replace last o+= for raw noise -> -15 chars

void mainImage(out vec4 o, vec2 u) {
    vec2 U = 4.* u/iResolution.y, s = u-u, v = s++, f,r;
    for(  vec4 h, K = vec4(0,157,1,158);
          o.w++ < 5.;
          v += mix( r = mix( h.yx, h.wz, f.x ), r.xx, f ) / s )
        f  = fract( U += U ), s += s,
        h  = fract( sin( dot( U -f, K.zy ) + K ) *57. ),
        f *= f*(3.-f-f);
    o += sin(v.y*20.+iTime);
    /* o += v.y; */
}


