#define CELL_SIZE 0.1

float rand(in vec2 uv) {
    return fract(814.*sin(uv.x*15829.+uv.y*874.));
}

float occupancy(in vec2 id) {
    return step(0.5, rand(id));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x/CELL_SIZE;

    vec2 id = floor(uv);
    vec2 id2 = floor(uv + 0.5);
    //vec2 modpos = fract(uv);
    vec2 modpos2 = fract(uv + 0.5)-0.5;

    vec3 offsets = vec3(-1.,0.,1);
    float height = 1e6;
    height = mix(height, min(height, length(max(vec2(0.), -modpos2))), occupancy(id2));
    //height = mix(height, min(height, length(max(vec2(0.), -modpos2))), occupancy(id2));
    //height = mix(height, min(height, length(max(vec2(0.), -modpos2*offsets.xx))), occupancy(id2+offsets.xx));
    // Output to screen
    fragColor = vec4(vec3(occupancy(id) + height*1.5),1.0);
}
