float cubicInOut(float k) {
    if ((k *= 2.0) < 1.0) {
        return 0.5 * k * k * k;
    }

    return 0.5 * ((k -= 2.0) * k * k + 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r = 0.5;
    if(uv.y > 0.75) {
        r = uv.x;
    } else if(uv.y > 0.5) {
        r = smoothstep(0.0, 1.0, uv.x);
    } else if(uv.y > 0.25) {
        r = smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, uv.x));
    } else {
        r = cubicInOut(uv.x);
    }
    vec3 col = vec3(r);

    // Output to screen
    fragColor = vec4(col,1.0);
}
