//Power Brot Zoom by eiffie (SuperFractalThingy Math for higher powers, delta not approx)
//#define PWR 3
#define PWR 4
//#define PWR 5

#define rez iResolution.xy

vec2 cpwr(vec2 C, float pwr){
  float a=atan(C.y,C.x)*pwr;
  return pow(length(C),pwr)*vec2(cos(a),sin(a));
}
vec2 cmul(vec2 A, vec2 B){return vec2(A.x*B.x-A.y*B.y,A.x*B.y+A.y*B.x);}

float tym,scale,yFlip;
float mndfk(vec2 dZ){
  dZ*=scale;
#if(PWR==3)
  vec2 Z=vec2(-.5448069,.27999981*yFlip);
#elif(PWR==4)
  vec2 Z=vec2(-.7194003,.27*yFlip);
#elif(PWR==5)
  vec2 Z=vec2(-.755805,.2*yFlip);
#endif
  vec2 dC=dZ,C=Z;
  float iters=32.+tym*8.;
  float i=0.;
  for(i=0.;i<iters;i+=1.){
#if(PWR==3)
    dZ=3.*cmul(cpwr(Z,2.),dZ)+3.*cmul(Z,cpwr(dZ,2.))+cpwr(dZ,3.)+dC;
    Z=cpwr(Z,3.)+C;
#elif(PWR==4)
    dZ=4.*cmul(cpwr(Z,3.),dZ)+6.*cpwr(cmul(Z,dZ),2.)+4.*cmul(Z,cpwr(dZ,3.))+cpwr(dZ,4.)+dC;
    Z=cpwr(Z,4.)+C;
#elif(PWR==5)
    dZ=5.*cmul(cpwr(Z,4.),dZ)+10.*cmul(cpwr(Z,3.),cpwr(dZ,2.))+10.*cmul(cpwr(Z,2.),cpwr(dZ,3.))+5.*cmul(Z,cpwr(dZ,4.))+cpwr(dZ,5.)+dC;
    Z=cpwr(Z,5.)+C;
#endif
    if(dot(dZ,dZ)>40.)break;
  }
  return ((iters-i)+log2(log2(dot(dZ,dZ))/log2(40.00))/log2(float(PWR)))/iters;
}
void mainImage(out vec4 O,in vec2 U){
  vec2 uv=(2.0*U-rez)/rez.x;
  tym=mod(iTime,110.)-55.;
  yFlip=sign(tym);
  tym=55.-abs(tym);
  scale=exp(-tym*.5);
  float a=mndfk(uv);
  O=vec4(a,a,a,1.0);
}
