/**********************************************/
/**********************************************/
/**********************************************/
/*                                            */
/*     /*\     Parameters can be changed in   */
/*    /* *\    "Common" tab.                  */
/*   /* | *\                                  */
/*  /*  o  *\  Avoid modifying this file.     */
/* /*********\                                */
/*                                            */
/**********************************************/
/**********************************************/
/**********************************************/

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 tex  = fragCoord / iResolution.xy ;   
    vec4 base = textureLod(iChannel0,tex,0.0);

    float diff = 0.0;
#if DRAW_LINES == 1
    int width = 1;

    for (int i = -width; i <= width; i++) {
        for (int j = -width; j <= width; j++) {
            vec4 color = textureLod(iChannel0, tex + vec2(i, j)/iResolution.xy, 0.0);
            if (color != base) {
                diff += 0.2;
            }
        } 
    }
#endif

    fragColor = vec4(base.rgb - vec3(diff),0.0);
}

// M and N parameters
#define N 79.0
#define M 53.0

// Split or distortion (1 or 0)
#define DO_SPLITS 1

// Lines or not
#define DRAW_LINES 0

// Define how much "zoom"
#define EXTENT 20.0

/**********************************************/
/**********************************************/
/**********************************************/
/*                                            */
/*     /*\                                    */
/*    /* *\                                   */
/*   /* | *\  Do not modify below this point. */
/*  /*  o  *\                                 */
/* /*********\                                */
/*                                            */
/**********************************************/
/**********************************************/
/**********************************************/

const float log_step = N/M;

/**********************************************/

vec4 getRandomColor(uvec4 id) {
    vec3 tmp = vec3(fract(float(id.x^id.w)/213789.0), fract(float(id.y^1489u)/348789.0), fract(float(id.z^2789u)/5689.0));
    for (int i = 0; i < 16; i++) {
      tmp = fract(vec3(13.14159265359,1.41421356237,6.62607004) * tmp.zyx + tmp.yxz / vec3(13.14159265359,1.41421356237,6.62607004));
    }
    return vec4(tmp, 1.0);
}

/**********************************************/

float quantize(float d) {
    return pow(log_step,floor(log(d)/log(log_step)));
}

/**********************************************/

int d2level(float d) {
    return int(floor(log(d)/log(log_step)));
}

/**********************************************/

float level2d(int level) {
  return pow(log_step,round(float(level)));
}

/**********************************************/

float interpolate(float d) {
    int   level   = d2level(d);
    float df      = level2d(level);
    float dc      = level2d(level-1);    
    float linear  = (d - df) / (df - dc) / log_step;
    return smoothstep(0.0,1.0,linear);
}

/**********************************************/

float spacing(float d) {
    return 1.0/d;   
}

/**********************************************/

float shift(float d) {
    if (N == 2.0) {
        return spacing(d) / 2.0;
    }
    return d ;
}

/**********************************************/

float id2Lborder(int id, float d) {
    return (float(id) +       (shift(d) / spacing(d)) ) * spacing(d);
}

/**********************************************/

float id2center(int id, float d) {
    return (float(id) + 0.5 + (shift(d) / spacing(d)) ) * spacing(d);
}

/**********************************************/

int u2id(float u, float d) {
    return int(floor( u / spacing(d) - (shift(d) / spacing(d)) ));
}

/**********************************************/

int u2closestLBorderId(float u, float d) {
    int lid = u2id(u, d);
    if (u < id2center(lid, d)) {
        return lid;
    } else {
        return lid+1;
    }
}

/**********************************************/

float u2closestBorder (float u, float d) {
    return id2Lborder(u2closestLBorderId(u,d), d);
}

/**********************************************/

int getLocalID(float u, float d, out float ctr) {
  // fine and coarse densities
  int   level = d2level(d);
  float df    = level2d(level  );
  float dc    = level2d(level-1);
  // enclosing band id
  int   lid     = u2id(u, df); 
  // get the borders of the fine band
  float lf_brdr = id2Lborder(lid, df);
  float rf_brdr = id2Lborder(lid+1, df);
  // get the closest coarser band borders
  float lc_brdr = u2closestBorder(lf_brdr, dc);
  float rc_brdr = u2closestBorder(rf_brdr, dc);
  // interpolate between fine and coarse borders
  float a     = interpolate(d);
  float left  = lf_brdr * a + lc_brdr * (1.0 - a);
  float right = rf_brdr * a + rc_brdr * (1.0 - a);    
  // adjust enclosing band after interpolation
  if      (u < left ) lid--;
  else if (u > right) lid++;

  return lid;
}

/**********************************************/

uvec4 setId(uvec4 id,uint bit) // 128 bits IDs
{
    if (bit > 127u) return id;
    if (bit < 64u) {
        if (bit < 32u) { // 0 - 31 (w) LSBs
            return uvec4(id.x,id.y,id.z,id.w | (1u << bit));
        } else {         // 32 - 63 (x)
            return uvec4(id.x,id.y,id.z | (1u << (bit-32u)),id.w);            
        }
    } else {
        if (bit < 96u) { // 64 - 95 (x)
            return uvec4(id.x,id.y | (1u << (bit-64u)),id.z,id.w);            
        } else {         // 96 - 127 (x) MSBs
            return uvec4(id.x | (1u << (bit-96u)),id.y,id.z,id.w);            
        }
    }
}

/**********************************************/

uvec4 getGlobalID(int lid, float d) {
  uvec4 gid = uvec4(0u);
  int start_level = d2level(d);
  for (int level = start_level; level > 0; level--) {
    float df      = level2d(level);
    float dc      = level2d(level-1);
    float lf_brdr = id2Lborder(lid  , df);
    float rf_brdr = id2Lborder(lid+1, df);
    // get the closest borders on the coarse bands
    int lid_brdr = u2closestLBorderId(lf_brdr, dc);
    int rid_brdr = u2closestLBorderId(rf_brdr, dc);
    if (lid_brdr == rid_brdr) {
      // this band is closing between parents, tag in ID (lower 96 bits)
      gid = setId(gid,uint(96 - level));
      lid  = u2id(lf_brdr, dc);
    } else {
      // move up to parent
      lid  = lid_brdr;

    }      
  }
  // parent ids go into the most significant 32 bits
  gid.x = uint(lid);
  return gid;
}

/**********************************************/


//////////////////////////////////////////////////////
//////////////////////////////////////////////////////
// Noise from https://www.shadertoy.com/view/4sdGD8 //
//////////////////////////////////////////////////////
//////////////////////////////////////////////////////

lowp vec3 permute(in lowp vec3 x) { return mod( x*x*34.+x, 289.); }
lowp float snoise(in lowp vec2 v) {
  lowp vec2 i = floor((v.x+v.y)*.36602540378443 + v),
      x0 = (i.x+i.y)*.211324865405187 + v - i;
  lowp float s = step(x0.x,x0.y);
  lowp vec2 j = vec2(1.0-s,s),
      x1 = x0 - j + .211324865405187, 
      x3 = x0 - .577350269189626; 
  i = mod(i,289.);
  lowp vec3 p = permute( permute( i.y + vec3(0, j.y, 1 ))+ i.x + vec3(0, j.x, 1 )   ),
       m = max( .5 - vec3(dot(x0,x0), dot(x1,x1), dot(x3,x3)), 0.),
       x = fract(p * .024390243902439) * 2. - 1.,
       h = abs(x) - .5,
      a0 = x - floor(x + .5);
  return .5 + 65. * dot( pow(m,vec3(4.))*(- 0.85373472095314*( a0*a0 + h*h )+1.79284291400159 ), a0 * vec3(x0.x,x1.x,x3.x) + h * vec3(x0.y,x1.y,x3.y));
}

/**********************************************/
/**********************************************/
/**********************************************/
/*                                            */
/*     /*\                                    */
/*    /* *\                                   */
/*   /* | *\  Do not modify below this point. */
/*  /*  o  *\                                 */
/* /*********\                                */
/*                                            */
/**********************************************/
/**********************************************/
/**********************************************/

////////////////////////
// fields for figures //
////////////////////////
vec2 uv(vec2 p) {
    vec2 f = vec2(p) + 14.0 * vec2(snoise(p.xy*0.015 + 0.04*iTime),
        						   snoise(p.xy*0.015 + 0.25*iTime));
    return f.xy;
}

vec2 d(vec2 p) {
#if DO_SPLITS == 0
    return vec2(log_step);
#endif
    float delta = 1.0;
    vec2  dudx  = ( uv(p - vec2(delta,0.0)) - uv(p + vec2(delta,0.0)) ) * 0.5 / delta;
    vec2  dudy  = ( uv(p - vec2(0.0,delta)) - uv(p + vec2(0.0,delta)) ) * 0.5 / delta;
    float du    = log_step / (0.000001 + length(vec2(dudx.x,dudy.x)));
    float dv    = log_step / (0.000001 + length(vec2(dudx.y,dudy.y)));
    du = max(log_step,du);
    dv = max(log_step,dv);
    return vec2(du,dv);
}

//////////////////
// How to draw  //
//////////////////

vec4 draw(vec2 p) {
    float _ = 0.0;

    vec2 u   = uv(p);
    vec2 d   = d(p);
    int lidA = getLocalID(u.x, d.x, _);
    int lidB = getLocalID(u.y, d.y, _);

    float bu = id2Lborder(lidA,d.x);
    float bv = id2Lborder(lidB,d.y);

    uvec4 gidA = getGlobalID(lidA, d.x);
    uvec4 gidB = getGlobalID(lidB, d.y);

    return (getRandomColor(gidA) + getRandomColor(gidB))*0.5;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 p = EXTENT * fragCoord / iResolution.y ;
    fragColor = draw( p );
}
