#define PI 3.14159265358979323846

float rand1(in float f)
{
    return fract(sin(f)*100000.0);
}

float random(in vec2 _st)
{
    return fract(sin(dot(_st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ) 
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float fFrame = float(iFrame);

    fragColor = vec4(hsb2rgb(vec3(0.7, 0.6, uv.y / 1.4)), 1.0);    

    float WIDTH = iResolution.x / 4.;
    for (float i = 0.; i < WIDTH; i++) {
        // radius of a ball
        float r = 10.;
        float hspace = iResolution.x / WIDTH;
        // some dancing, some horizontal space
        float x = iResolution.x / 60.0 * sin(PI / 180. * fFrame) + hspace * i - hspace  * rand1(i);
        float movement = fFrame * iResolution.y / 360.;
        // movement, some vertical space
        float y = movement + iResolution.y * rand1(i);
        	  y = mod(-abs(y), iResolution.y + r);

        vec2 ball = vec2(x, y);
        vec2 ballDist = fragCoord - ball;

        fragColor = mix(fragColor, vec4(hsb2rgb(vec3(0.20, .9, .9)), 1.0),
                        1.-smoothstep(
                            ball.y / 10., ball.y / 2.,
                            dot(ballDist, ballDist)));
    }
}
