float circleDist( in vec2 uv, in vec2 pos, in float r ) {
    return length(uv - pos) - r;
}

float boxDist( in vec2 p, in vec2 a, in vec2 b, in float r ) {
    vec2 ba = b-a, pa = p-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    vec2  q = pa-h*ba;
    float d = length(q);
    return d - r;//vec3(d-r,q/d);
}

float circleScene(in vec2 p) {
    float d = 100000.0;
    for (float i = 0.0; i < 500.0; ++i){
        float c = circleDist(p, vec2(-1.0 + i * 0.05, -0.8 + i * 0.05), 0.05);
        float c2 = circleDist(p, vec2(1.0  + i * 0.02, 0.0 + i  * 0.02), 0.05);
        d = min(d, min(c, c2));
    }
    return d;
}

float segmentScene(in vec2 p) {
    float d = 1000000.0;
    for (float i = 0.0; i < 500.0; ++i){
        vec2 c  = vec2(-1.0 + i * 0.05, -0.8 + i * 0.05);
        vec2 c2 = vec2(1.0  + i * 0.02, 0.0 + i  * 0.02);
        float b = boxDist(p, c, c2, 0.005);
        d = min(d, b);
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	float d = circleScene(p);
    float d2 = segmentScene(p);

	// color circles ...
    vec3 col = vec3(0.9,0.1,0.1);
    if (d > 0.0) { 
        col = vec3(1.0,1.0,1.0) * (pow(d, 0.25));
    }

    // draw the connections ...
    if (d2 < 0.0 && d2 < d) {
        col = vec3(0.0, 0.0, 0.0);
    }

	fragColor = vec4(col,1.0);
}
