// Commented refactored fork of "Crowdy waves" by rory618. https://shadertoy.com/view/ttyGWw
// All-in-one-buffer variant of the Voronoi particle tracking method: https://www.shadertoy.com/view/wlcXRS

// more refs: https://www.shadertoy.com/results?query=voronoi%20particle%20tracking&sort=newest
//            rory618's: https://www.shadertoy.com/user/rory618 [which one seminal ?]
//            wyatt's: https://www.shadertoy.com/results?query=wyatt [which one seminal ?] https://www.shadertoy.com/view/MlVfDR
//            https://www.shadertoy.com/view/4sK3WK
// Another refactored/commented (very different) one: https://www.shadertoy.com/view/WtK3zt

void mainImage( out vec4 O, vec2 I )
{
    O = keyFlip(32)
        ? T1(I)/(R.x*R.y)*N    // space key: draw Id/Voronoi
        : T2(I);               // draw trace
}

// === Physics: manage particles pos+V: advect & react
// see also: Boids physics https://www.shadertoy.com/results?query=boids

void mainImage( out vec4 O, vec2 I )
{
    O = T0(I);              // previous state

    O.xy = mod(O.xy,R);     // cyclical world
  //vec4 r = rand4( int(I.x) + int(I.y)*2048 + iFrame*2048*2048);   // deterministic
  //vec4 r = rand4( int(I.x) + int(I.y)*2048 + (int(iTime*2048.)+iFrame)*2048);
    vec4 r = rand4( int(I.x) + int(I.y)*2141 + (int(iTime*2141.)+iFrame)*2141); // without 2048 bias

    if(iFrame<3)            // init: random location, random V , |V|= 1/4
        O.xy = r.xy*R,
        O.zw = .25*cos(TAU*(vec2(0,.25)+r.z));


#if 1                       // --- emulate pressure & viscosity (is also boids/schoolfish coeherence)
    vec4 a = T1(O.xy);      // get the 4 ids in Vorono誰 buffer at particle location
    vec2 ns = vec2(0),      //         (should be the closest)
         df = vec2(0), D;
    for(int i = 0; i < 4; i++){ 
        vec4 n = A(a[i]);
        ns += n.zw/4.;      // average velocity
        D = O.xy - n.xy;
        D = mod( D + R/2., R ) - R/2.;
                            // repulsed by nearby particles  
        if( l2(D) > l2(.005) && l2(D) < l2(10.) )
        	df += normalize(D)/(length(D)+.03);
    }
    O.zw += df/25.;         // pressure: apply repulsion force
    O.zw = mix(O.zw,ns,.1); // visc: V = average Vpartic + relax(oldV+centering)
#endif

    if(l2(O.zw) > l2(.001)) // |V| = 1/4 + relax(oldV)
    	O.zw = mix(O.zw, normalize(O.zw)/4.,.05);

    O.zw += randn(r.xy)/1e2; // --- add a bit of random force [init/resized=superimposed]

    if(iMouse.z > 0.){         // --- mouse action on particles
        vec2 D = O.xy-iMouse.xy, F = D;           // default: repulse
        if keyDown(64+4 ) F = -D;                 // "D": drain
        if keyDown(64+19) F = vec2( -D.y, D.x);   // "S": swirl
        if keyDown(64+16) F = iMouse.xy-iMouse.zw;// "P": push
        O.zw += normalize(F+1e-5)/(length(D)+.03);
    }
    O.xy += O.zw;  // pos += V  (indeed V contains dx/dt )

}

// === Vorono誰 buffer: manage tracking of particles Ids

// --- insert (i,d) and maintain the 4 closest (i_,d_) 
void list_insert(inout vec4 i, inout vec4 d, float i_, float d_){	
    if(i_ == 0.) return;           // not a particle : exit
    if(any(equal(vec4(i_),i))) return; // already in top4 : exit
    if     (d_ < d[0])             // closer to closest: insert here
        i = vec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             // closer to 2nd closest: insert here
        i = vec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])             // closer to 3rd closest: insert here
        i = vec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])             // closer to 4th closest: insert here
        i = vec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

void mainImage( out vec4 O, vec2 I )
{
    vec4  i = vec4(0),
         i0 = T1( I ),             // 4 closests particles here and around
         ia = T1( I + vec2( 1, 0) ),  // NB: could use an array.
         ib = T1( I + vec2( 0, 1) ),
         ic = T1( I + vec2(-1, 0) ),
         id = T1( I + vec2( 0,-1) );

//#define dist(i) length( A(i).xy - I )
//#define dist(i) length( mod( A(i).xy-I + R/2., R) - R/2. )  
vec2 D;
#define dist(i) ( D = mod( A(i).xy-I + R/2., R) - R/2., dot(D,D) )

    vec4  d = vec4(1e9); 
    for(int k = 0; k < 4; k++){    // sorts all these
        list_insert( i, d, i0[k], dist(i0[k]) );
        list_insert( i, d, ia[k], dist(ia[k]) );
        list_insert( i, d, ib[k], dist(ib[k]) );
        list_insert( i, d, ic[k], dist(ic[k]) );
        list_insert( i, d, id[k], dist(id[k]) );
    }
#if 0 // also checking diagonal (to test possibly axis bias)
    ia = T1( I + vec2( 1, 1) ),
    ib = T1( I + vec2(-1, 1) ),
    ic = T1( I + vec2( 1,-1) ),
    id = T1( I + vec2(-1,-1) );
    for(int k = 0; k < 4; k++){    // sorts all these
        list_insert( i, d, ia[k], dist(ia[k]) );
        list_insert( i, d, ib[k], dist(ib[k]) );
        list_insert( i, d, ic[k], dist(ic[k]) );
        list_insert( i, d, id[k], dist(id[k]) );
    }
#endif

    for(int k = 0; k < 1; k++){    // try to re-insert some random particle (possibly escaped from tracking)
      //int r = IHash( int(I.x) + int(I.y)*2048 + iFrame*2048*2048 +k*11131); //deterministic
        int r = IHash( int(I.x) + int(I.y)*2141 + iFrame*2141*2141 +k*11131); //without 2048 bias
      //int r = IHash( int(I.x) + int(I.y)*2048 + (int(iTime*2048.)+iFrame)*2048 +k*11131);
        int i_ =  1 + r % ( int(R.x*R.y)/int(N) ); // [ why /10? ]
        list_insert(i, d, float(i_), dist(i_) );
    }
    O = vec4(i);                   // stores 4 closest

}

#define N 10. // use only 1/N % of the max Rx*Ry particles (for readability)

// Buff B (1) store Vorono誰 tracking acceleration structure;
//            xyzw: ids of 4 closest partics to buffer location
// Buff A (0) stores particles: 
//            xy: position zw: velocity

// --- translate particle id (in [1,Rx*Ry] ) to buffer pixel 
#define A(n) T0( vec2( (int(n)-1) % iR.x,      \
                       (int(n)-1) / iR.x ) +.5 )  // +.5 useless

// --- utils

#define R     iResolution.xy
#define iR    ivec2(iResolution)
#define T0(U) texelFetch( iChannel0, ivec2(U)   , 0 )
#define T1(U) texelFetch( iChannel1, ivec2(U)%iR, 0 )
#define T2(U) texelFetch( iChannel2, ivec2(U)   , 0 )

#define l2(x) dot(x,x)

#define TAU 6.2831853

#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

// --- keyboard
#define key(k,mode) ( texelFetch( iChannel3, ivec2(k,mode), 0 ).x > .5 )
#define keyDown(k) key(k,0)
#define  keyHit(k) key(k,1)
#define keyFlip(k) key(k,2)


// --- random numbers

int IHash(int a){
	a = (a ^ 61) ^ (a >> 16);
	a = a + (a << 3);
	a = a ^ (a >> 4);
	a = a * 0x27d4eb2d;
	a = a ^ (a >> 15);
	return a;
}

#define Hash(a) ( float(IHash(a)) / float(0x7FFFFFFF) ) // Uniform in [0,1]

vec4 rand4(int seed){
    return vec4(Hash(seed^0x34F85A93),
                Hash(seed^0x85FB93D5),
                Hash(seed^0x6253DF84),
                Hash(seed^0x25FC3625));
}

// --- normal law random generator
vec2 randn(vec2 r){ // r: randuniform
    r.x = sqrt( -2.* log(1e-9+abs(r.x)));
    r.y *= TAU;
    return r.x * vec2(cos(r.y),sin(r.y));
}

// === draw + blend with fading past

void mainImage( out vec4 O, vec2 I )
{
    O = vec4(0);
    vec4 a = T1(I), P;         // 4 particule id (supposed to be particles closest to I)

    for(int i = 0; i < 4; i++) // draw Gaussian blobs
        P = A(a[i]),
        O += .4* exp( -.5* l2( I - P.xy ) ) 
               * ( keyFlip(64+3) ? hue( length(P.zw) ) : vec4(1));

    O = mix(O, T2(I), .9);     // blend with fading past
  //O = mix(O, texture(iChannel2,-.01+1.02*I/R), .9); // false-3D variant
}
