//por jorge2017a1-
#define MAX_STEPS 100
#define MAX_DIST 100.
#define MIN_DIST 0.001
#define EPSILON 0.001

vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material);
vec3 getMaterial( vec3 pp, float id_material);

float iTimeGlobal;

vec3 light_pos1   ;
vec3 light_color1 ;
vec3 light_pos2   ;
vec3 light_color2 ;
//operacion de Union
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )
float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }

///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}

float unionSDF(float distA, float distB)
	{ return min(distA, distB);}

float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}
//-------------------------------------------
//----------oPeraciones de Repeticion
float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}
///------------------------------------
// object transformation
vec3 rotate_x(vec3 p, float phi)
{
    float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
///------------------------------------
vec2 rotatev2(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

///------------------------------------
vec3 GetDist(vec3 p  ) 
{	
	vec3 res;

    if (iTimeGlobal<5.0)
    {

        p=p+vec3(14.0+iTimeGlobal,0.0,0.0);

    }
    else if(iTimeGlobal<15.0)
    {
        p=p+vec3(18.0,0.0,0.0+iTimeGlobal*2.0);
     }else if(iTimeGlobal>15.0)
     {
        p=p+vec3(18.0,0.0,0.0-iTimeGlobal*5.0);
     }



    vec3 pp=p;
    res = vec3(9999.0, -1.0,-1.0);


    p= rotate_z(p-vec3(0.0,0.0,0.0), radians(45.0));
    p= rotate_x(p-vec3(0.0,0.0,0.0), radians(90.0));

	 vec3 repcA=p;
     repcA.x= opRep1D( repcA.x, 5.0 );
     repcA.y= opRep1D( repcA.y, 8.0 );


     vec3 pnewA=p;
     pnewA.y= opRep1D( pnewA.y, 10.0 );

    float db1A= sdBox( pnewA-vec3(0.0), vec3(10.0,20.0,10.0) );
    float db2A= sdBox( repcA-vec3(0.0), vec3(2.0,2.0,15.0) );
    db1A= differenceSDF( db1A,db2A);


    ///-------------

     vec3 posmov=vec3(30.0,0.0,0.0);
    vec3 repcB=p-posmov;
     repcB.x= opRep1D( repcB.x, 8.0 );
     repcB.y= opRep1D( repcB.y, 8.0 ); 


    vec3 pnewB=p-posmov;
    pnewB.y= opRep1D( pnewB.y, 10.0 );
    float db1B= sdBox( pnewB-vec3(0.0), vec3(10.0,20.0,15.0) );

    float db3B= sdBox( repcB-vec3(0.0), vec3(15.0,20.0,5.0) );
    float db2B= sdBox( repcB-vec3(0.0), vec3(2.0,2.0,20.0) );


    float dif3= unionSDF(db3B,db2B);
    dif3= intersectSDF(db1B,dif3);


    res =opU3(res, vec3(db1A,-1.0,3.0)); 
    res =opU3(res, vec3(dif3,-1.0,4.0)); 
    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}


vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro) 
{
    vec3 l = lp - p;
    float dist = max(length(l), 0.01);
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= dist;

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    float dif = clamp(dot(l, n), 0.0, 1.0);
    float spe = pow(clamp(dot(r, -rd), 0.0, 1.0), 8.0);
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);

    vec3 lin = vec3(0.2);
    lin += 1.0*dif*vec3(1, .97, .85);
    lin += 2.5*spe*vec3(1, .97, .85)*dif;
    lin += 2.5*fre*vec3(1);
    lin += 0.5*dom*vec3(1);

    return lin*atten;
}



//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}
//---------actualizacion por Shane---28-may-2020    ...gracias
float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    for(int i=0; i<MAX_STEPS; i++) 
    {
    	p = ro + rd*dO;
        dS = GetDist(p);

        if(dO>MAX_DIST || abs(dS.x)<MIN_DIST) break;
        dO += dS.x;
        marchCount++;

    }

    mObj.dist = dO;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    return dO;
}

//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l );
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
//----------------------------------------------------

//-------------------------------
vec3 Getluz(vec3 p, vec3 ro, vec3 rd, vec3 nor , vec3 colobj ,vec3 plight_pos)
{
    float intensity=1.0;
     vec3 result;
     result = lightingv3( nor, p, plight_pos,  rd,ro)*colobj*6.0;

    if (mObj.blnShadow==true)
    {
    	float fhadow=GetShadow(p,plight_pos);
    	return result*fhadow;

     }
    else
    {  return result; }
}
//------------------------------------------------
vec3 getMaterial( vec3 pp, float id_material)
{
  vec3 col=vec3(1.0);
  vec3 p=pp;


    if (id_material==3.0)
    { return vec3(0.952, 0.32, 0.42) *1.25; }


    if (id_material==4.0)
    { return vec3(0.52, 0.82, 0.42) *1.25; }

    if (id_material==5.0)
    { // nothing hit: background gradient
     return vec3(0.2, 0.2, 0.2) * (-mObj.uv.y+1.2);
    }



}
//-------------------------------------------------

vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material)
{
   	vec3 colobj; 
    if (id_color<100)
		{ colobj=getColor(int( id_color)); }

    if (id_material>-1.0 && id_color==-1)
        { 
            colobj=vec3(0.5);
            colobj*=getMaterial(p, id_material); 
            return colobj;
        }
    return colobj;
}




///---------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
   mObj.uv=uv;
    float t;
    t=mod(iTime*2.0,30.0);
    iTimeGlobal=t;

    itime=t;
	//mObj.blnShadow=false;
    mObj.blnShadow=true;

 	light_pos1= vec3(10.0, 6.0, 10.0 );  light_color1 = vec3( 1.0 );
 	light_pos2= vec3( -5.0, 6.0, -5.0 ); light_color2 = vec3( 1.0, 1.0, 1.0 );

    vec3 ro=vec3(0.0);

    vec3 rd=normalize(vec3(uv,1.0));
    if (iTimeGlobal>15.0 &&  iTimeGlobal<22.0 )
    {
        rd.xz= rotatev2(rd.xz, radians(160.0));    
    }
    else if(iTimeGlobal>22.0 &&  iTimeGlobal<30.0 )   
   {    
        ro=vec3(8.0,10.0,5.0);
   }

    light_pos1+=ro;
    light_pos2+=ro;


    vec3 col = vec3(0);
    TObj Obj;
    mObj.rd=rd;
    mObj.ro=ro;

    float d = RayMarch(ro, rd);
    Obj=mObj;
    vec3 p = (ro + rd * d ); 

    mObj.p=p;
    mObj.dist =d;
    vec3 nor=mObj.normal;

    vec3 colobj;
    colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);


	float dif1=1.0;
   	vec3 result;
    result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
    result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
    result/=1.25;
    col= result*dif1;



  	//sugerencia por dean_the_coder,
    //col *= 1.0 - pow(d / 100.0, 1.5);
    col *= 1.0 - pow(d /(MAX_DIST) , 3.5);    
    col = pow(col, vec3(1.0/2.2));  
    fragColor = vec4(col,1.0);
}
