vec3 rainbow(float x) {
  vec3 c = vec3(x, .5, 1.);
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord/iResolution.xy*vec2(iResolution.x/iResolution.y,1.0))*2.0-1.0;
    float outer_edge = 1.0-step(1.0,length(uv));
    float edge_1 = step(0.25,length(uv));
    float inner_edge = step(0.1,length(uv));

    float angle = atan(uv.y,uv.x)+iTime;

    float c = pow((sin(4.*angle)+1.)/2.,(sin(iTime+angle*2.)+1.0)*3.);
    vec3 color = rainbow((0.6-c)+sin(angle)+iTime);

    fragColor = 
        outer_edge*edge_1*vec4(c*color+(1.-c)*vec3(0.25,0.24,0.2),1.0)
        +(1.0-edge_1)*inner_edge*(vec4(vec3(0.4+0.3*c),1.0));
}
