#define rot(a) mat2(cos(a + vec4(0,33,11,0)))

vec2 p1;

float sdDmd(vec2 p,vec2 sz,float a){
	p = abs(p * rot(a)) - sz;
	return max(p.x,p.y);
}

float scene(vec2 p){
	float f = 0.25, t = iTime;
	p1 = vec2(sin(t),cos(t))*(sin(t)*0.05+0.1);
	return -f*log2(exp2(-length(p-p1)/f)-exp2(-length(p+p1)/f));
}

void mainImage(out vec4 fragColor,vec2 fragCoord){
	vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.0);

	float v = scene(uv);
	col = 0.0025*(vec3(3,0,1)/length(uv-p1) + vec3(1,0,3)/length(uv+p1));
	col += col*10.0*smoothstep(1.0/iResolution.y,-1.0/iResolution.y,sdDmd(mod(uv+0.025,0.05)-0.025,vec2(0.02,0.002),-atan(dFdy(v),dFdx(v))));

    fragColor = vec4(col,1.0);
}
