/*
I watched a video of a mandala booklet creating spiraling images, known as
Spiroglyphs. I thought it was a pretty neat effect, so I created a shader
to mimic the effect. 
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // These are the normalized pixel coordinates (from 0 to 1)
    vec2 uvTex = fragCoord/iResolution.xy;

    // This samples the texture to control the line thickness
    float video = texture(iChannel0, uvTex).x;

    // This controls the minimum and maximum line thickness
    float thickness = (video - 0.25)*1.5;

    // These are the square UVs with (0,0) in the center
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y * 2.0; 

    // Use the resolution to control how many loops we do
    float loops = 25.0;

    // This generates a gradient of a spiral  
    float angle = atan(uv.y, uv.x);
    float radius = length(uv) * loops;
    float spiral = radius + angle * 0.1591549430918; // 1/(2*pi)

    // This finds the partial derivative of the spiral for a quasi AA effect
    float aa = fwidth(radius) * 3.0;

    // This generates a line with varying thickness with AA smoothing
    float line = smoothstep(thickness+aa, thickness-aa, abs(fract(spiral)-0.5)*2.);

    // This applies softer colors for black and white
    vec3 black = vec3(0.004,0.161,0.306);
    vec3 white = vec3(1.000,0.953,0.910);
    vec3 color = mix(black, white, line);

    // This outputs to the screen
    fragColor = vec4(color, 1);
}
