float f_n_dist_line(
    vec3 a, 
    vec3 b, 
    vec3 p
){

    vec3 ab = b-a;
    vec3 ap = p-a;
    float t_abs = (dot(ab,ap)/length(ab));
    float t_nor = t_abs / length(ab);
    vec3 l = a+t_nor*ab;
    float d = length(l-p);
    float s = sign(ab.x * ap.y - ab.y * ap.x);
    float sd = d*s;
    return sd;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/iResolution.y;



    vec3 a = vec3(0.0, 0.0, 0.0);
    vec3 b = vec3(0.75, 1.25, 0.0);
    float n = f_n_dist_line(a,b,vec3(o_trn.xy, 0.));

    float n_abs = abs(sin(99.*n));
    // n = smoothstep(0.1, 0.2, n);
    vec4 o_col = vec4(
        0.2 * n_abs, 
        0.8 * n_abs, 
        0.6 * n_abs, 
        1.
    );
    if(n < 0.){
        o_col = vec4(
            0.8 * n_abs, 
            0.2 * n_abs, 
            0.6 * n_abs, 
            1.
        );
    }

    fragColor = vec4(
        o_col
    );
}
