// --------------------------------------------------------

float sdArc( in vec2 p, in vec2 sc, in float ra, float rb ) {
    // sc is the sin/cos of the arc's aperture
    p.x = abs(p.x);
    return ((sc.y*p.x>sc.x*p.y) ? length(p-sc*ra) : abs(length(p)-ra)) - rb;
}

float sdHorseshoe( in vec2 p, in vec2 c, in float r, in vec2 w ) {
 //   p.x = abs(p.x);
    float l = length(p);
    p = mat2(-c.x, c.y, c.y, c.x)*p;
    p = vec2((p.y>0.0 || p.x>0.0)?p.x:l*sign(-c.x), (p.x>0.0)?p.y:l );
    p = vec2(p.x,abs(p.y-r))-w;
    return length(max(p,0.0)) + min(0.0,max(p.x,p.y));
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b ) { 
	if(qsdType == 0.) {
		vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
	}

	vec2 m = (a+b)/2.0;
	vec2 l = abs(b-a)/2.0;
	p = abs(p-m)-l;
	return length(p) * 0.2; 
}

float sdCircle( vec2 p, float r ) {
    return length(p) - r;
}

float sdBox( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
//    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
    return length(max(d,qsdf1)) + min(max(d.x,d.y),qsdf2); // 0.0);
}

float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th ) {
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
		q = mat2(d.x,-d.y,d.y,d.x)*q;
		q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}

float sdHexagram( in vec2 p, in float r ) {
    const vec4 k = vec4(-0.5,0.8660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}

float distanceFunction( in vec2 p, in vec2 a, in vec2 b ) { 
    int style = int(qsdStyle);
	if(style == 0) return sdSegment(p,a,b);
	if(style == 1) return sdCircle(p,qsdf1);
	if(style == 2) return sdOrientedBox(p,a,b,qsdf1);
	if(style == 3) return sdHexagram(p,qsdf1);
	if(style == 4) return sdHorseshoe(p,a,qsdf1,b);
	if(style == 5) return sdArc(p,a,qsdf1,qsdf2);
	if(style == 6) return sdBox(p,a);
 	return 0.;
}

