void displayData(inout vec4 fragColor, in vec2 fragCoord) {
    if(fragCoord.x > iResolution.x * 0.35) return; // no printing in that region
    if(fragCoord.y < iResolution.y * 0.95) return;

    Font f;
    setFont(f,iChannelResolution[3].xy / vec2(16, 16),iChannelResolution[3].xy);

    TextCursor t;
    const vec2 charScale = vec2(30,30);
    t.startPos = vec2(0, iResolution.y);
    t.coord = ivec2(0.);
    t.font = f;
    t.color = vec4(1.);
    t.charSize = charScale;
    t.charSpace = vec2(0.7, 1) * charScale;
    t.pageWidth = int(iResolution.x / t.charSize.x);
    t.fragCoord = fragCoord;

    int ifocus = int(focus); 
    int index = ifocus / 3;
    int field = ifocus % 3;

    printInt(ifocus+1,t);
    printChar(cSp,t);

    int legendIndex = ifocus * 8;
    for(int i=0;i<8;++i) {
        ivec2 chr = legends[legendIndex+i];
        if(chr == c0) break;
        printChar(chr,t);
    }

    bool isInteger = pData[ifocus].delta == INTEGER || pData[ifocus].delta == SLOWINTEGER;

    printChar(cSp,t);
    printFloat(data[index][field], t,isInteger ? 0 : 4);
    drawText(t, iChannel3, fragColor);
}

// animation routines ----------------------------------------

void cycleValue(int focusIndex,float deviation, float speed) {
    float ratio = 1.0 + cos(iTime * speed);  // 0 ... 2

    int index = focusIndex / 3;
    int field = focusIndex % 3;

    data[index][field] = clamp(data[index][field] -deviation + deviation * ratio, pData[focusIndex].vmin, pData[focusIndex].vmax);
}

void alterparams() {
    float amt = 0.1;

    cycleValue(2,  0.03, 0.1 );   // power
    cycleValue(10, 0.001, 0.15 ); // julia
    cycleValue(11, 0.003, 0.2 ); 
    cycleValue(12, 0.001, 0.25 ); // mult
    cycleValue(13, 0.003, 0.3 ); 

}

void animate() {
    if(animationEnable == 0.) return;
    alterparams();
}

