#define PI 3.141592654

vec3 palette(float loc, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b*cos( qcolor * (c*loc+d) );
}

vec2 complexPower(vec2 value, float power) {
    float rr = value.x * value.x + value.y * value.y; // radius squared
    if(rr == 0.0) return vec2(0.0001);

    float p1 = pow(rr, power / 2.0);
    float arg = atan(value.y, value.x);
    vec2 p2 = vec2( cos(power * arg), sin(power * arg));
    return p1 * p2;
}

int imod(int v, int d) { return v - (v/d)*d; }

vec3 calc(vec2 center,vec2 uv,inout float op, inout float om) {
	vec3 color4 = vec3(qc4+qrr,qc4+qgg,qc4+qbb);
	int equation = int(qequation);	
    int iterations = int(qiterations);    
	float dist;
    float closest = 2.;
    float closest2 = 4.;
    int smallestI = -1;
    vec2 z = uv;   
    vec2 c = center;  

    int equationStyle = int(qequation);

    if(equationStyle == 1) {
        c = center; 
        z = uv * 0.5; 
    }
    if(equationStyle == 2) {
        z = vec2(0.0); 
        c = uv * 0.5; 
    }
    if(equationStyle == 3) {
        z = center; 
        c = uv * 0.5; 
    }

    for (int i = 0; i < 1000; ++i) {
        if (i >= iterations) break;

        z = complexPower(z,qpower) + c;
        dist = distanceFunction(z,center+op,center-om);  

        if (dist < closest) {
            closest2 = closest;
            closest = dist;
            smallestI = i;
        } else if (dist < closest2) 
            closest2 = dist;

        c *= qcMult;
        z *= qzMult;
        op *= qoffsetMultP;
        om *= qoffsetMultM;
    }

    vec3 col = palette(float(smallestI) / qtint, vec3(qc1),vec3(qc2),vec3(qc3),color4);

    float shadow = sqrt(qshadowPower - closest / closest2);
    if(qflipShadow > 0.) shadow = 1.0 - shadow;

    float highlight = clamp(4. * (0.2 - (closest / closest2)), 0., 21.);

    col = col * shadow * qss + highlight;
    return col * (-1. / qiterations * float(smallestI) + 1.);
}

// --------------------------------------------------------

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    for(int i=0;i<PDATA_ADDR_COUNT;++i)  // get copy of storage data
        data[i] = fetchData(iChannel0, storeAddr[i]).xyz;
    animate();                           // optionally alter some of the values

    float zoomY = qzoom * iResolution.y / iResolution.x; // square aspect ratio
    vec2 vTexCoord = fragCoord / iResolution.xy;  // pixel coord mapped 0...1

    // pixel value panned and zoomed
	vec2 uv = vec2( (vTexCoord.x - qpanx - 0.5) * qzoom,  (vTexCoord.y - qpany - 0.5) * zoomY) * 0.5;    
    vec2 center = vec2(qcenterx,qcentery);
	float op = qoffsetP;
	float om = qoffsetM;

	vec3 color = vec3(0.);
    for(int i=0;i<4;++i)
        color += calc(center,uv,op,om);
	color *= 0.3;

    fragColor = vec4(vec3(0.5) + (color - vec3(0.5)) * qcontrast, 1.); // apply contrast

    if(displayCount > 0.) 
        displayData(fragColor,fragCoord);
}

