void printInt(int val, inout TextCursor t) {
    if(val < 0)
    {
        printChar(cMinus, t);
        val = -val;
    }

    int[maxStringLength] digits;
    int count = 0;

    for(;;)  {
        digits[count++] = val % 10;

        if(count >= maxStringLength)
            break;

        val /= 10;

        if(val == 0)
            break;
    }

    for(int i = count-1; i >= 0; i--)
        printChar(Digits[digits[i]], t);
}


void printFloat(float val, inout TextCursor t,int fractCount) {
    if(val < 0.0) {
        printChar(cMinus, t);
        val = -val;
    }

    int[maxStringLength] digits;
    int count = 0;

    int iVal = int(val);
    val -= float(iVal);

    for(;;) {
        digits[count++] = iVal % 10;

        if(count >= maxStringLength)
            break;

        iVal /= 10;

        if(iVal == 0)
            break;
    }

    for(int i = count-1; i >= 0; i--)
        printChar(Digits[digits[i]], t);

    if(fractCount == 0)  return;
    printChar(cDot, t);

    for(int i = 0; i < fractCount; i++) {
        val *= 10.0;

        int digit = int(val);
        val -= float(digit);

        printChar(Digits[digit], t);

        if(val <= 0.0)
            break;
    }
}

