void retrieveDataset(inout vec4 fragColor,vec2 fragCoord, const float storage[36]) {
    for(int i=0;i<12;++i) {
        if (storeDataAddr(fragCoord, storeAddr[i])) {
            int arrayIndex = i * 3;
            fragColor.x = storage[arrayIndex++];
            fragColor.y = storage[arrayIndex++];
            fragColor.z = storage[arrayIndex];
            return;
        }
    }
}

