void initializeStorage(inout vec4 fragColor,vec2 fragCoord) {
    if (storeDataAddr(fragCoord, FOCUS_ADDR)) fragColor.xyz = vec3(0.);

    int pIndex = 0;
    for(int i=0;i<PDATA_ADDR_COUNT;++i) {
        if(storeDataAddr(fragCoord, storeAddr[i])) {
            fragColor.xyz = vec3(0.);
            if(pIndex+0 < UI_SIZE) fragColor.x = pData[pIndex+0].start;
            if(pIndex+1 < UI_SIZE) fragColor.y = pData[pIndex+1].start;
            if(pIndex+2 < UI_SIZE) fragColor.z = pData[pIndex+2].start;
        }

        pIndex += 3;
    }
}

