const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
const int KEY_SHIFT = 16;
const int KEY_CTRL  = 17;
const int KEY_ALT   = 18;
const int KEY_PGUP  = 33;
const int KEY_PGDN  = 34;

bool keyClick(int ascii) {
	return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.);
}

bool getKeyState(int keyCode){
    return bool(texelFetch(iChannel1, ivec2(keyCode,0), 0).x != 0.);
}

bool getKeyClick(int keyCode){
    return bool(texelFetch(iChannel1, ivec2(keyCode,1), 0).x != 0.);
}

void acceleratedValue(inout float value) {
    bool aKey = getKeyState(65);
    bool zKey = getKeyState(90);

    if(aKey && zKey) value *= 50.; else
    if(aKey) value *= 0.1; else
    if(zKey) value *= 10.0;    
}

