bool alterFocusedVariable(int focus,int axis,inout vec3[PDATA_ADDR_COUNT] memory) {
    if(focus > UI_SIZE-1) return false;
    float changeAmount = 0.;
    int focusIndex = focus / 3;  // memory index
    int focusField = focus % 3;  // x,y,z field within

    if(getKeyClick(48)) {  // '0' sets value to 0.
        memory[focusIndex][focusField] = 0.;
        changeAmount = 1.;   // so function returns true
    }
    else {
        if(getKeyState(69)) { // 'E'
            changeAmount = sin(iTime) * 0.25;
        }
        else {
            if(axis == 0) { // arrow keys affect only focused variable 

                if(pData[focus].delta == SLOWINTEGER) { 
                    if(getKeyClick(KEY_LEFT))  changeAmount = -pData[focus].delta; else
                    if(getKeyClick(KEY_RIGHT)) changeAmount = +pData[focus].delta;
                } else {
                    if(getKeyState(KEY_LEFT))  changeAmount = -pData[focus].delta; else
                    if(getKeyState(KEY_RIGHT)) changeAmount = +pData[focus].delta;
                }
            }

            // mouse affects focused variable and also next one --------------
            if(iMouse.z > 0.0) { // left mouse button down
                vec2 delta = (iMouse.xy - abs(iMouse.zw)) * 0.01 * pData[focus].delta;
                if(axis == 0) changeAmount = delta.x;
                if(axis == 1) changeAmount = -delta.y; // flip Y axis
             }
        }

        if(changeAmount != 0.) {
            acceleratedValue(changeAmount);
            memory[focusIndex][focusField] += changeAmount;
        }
    }

    memory[focusIndex][focusField] = clamp(memory[focusIndex][focusField],pData[focus].vmin,pData[focus].vmax);

    return changeAmount != 0.;
}

