void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if(fragCoord.x > 20.0 || fragCoord.y > 1.0) return; // skip unused storage locations

    if(iFrame < 10 || keyClick(32)) {  // reset settings on coldstart or <Spc> keypress
        initializeStorage(fragColor, fragCoord);
        return; 
    }

    // $$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$
    if(keyClick(49)) {  // retrive storage dataset #1 via '1' key
        retrieveDataset(fragColor, fragCoord,sd1);
        return; 
    }
    if(keyClick(50)) {  // retrive storage dataset #2 via '2' key
        retrieveDataset(fragColor, fragCoord,sd2);
        return; 
    }
    if(keyClick(51)) {  // 3
        retrieveDataset(fragColor, fragCoord,sd3);
        return; 
    }
    if(keyClick(52)) {  // 4
        retrieveDataset(fragColor, fragCoord,sd4);
        return; 
    }
    if(keyClick(53)) {  // 5
        retrieveDataset(fragColor, fragCoord,sd5);
        return; 
    }
    if(keyClick(54)) {  // 6
        retrieveDataset(fragColor, fragCoord,sd6);
        return; 
    }
    if(keyClick(55)) {  // 7
        retrieveDataset(fragColor, fragCoord,sd7);
        return; 
    }
    // $$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$$

    // ==========================================================       
    // keyboard & mouse alter focused data (mouse also affects next entry) ---------
    vec3[PDATA_ADDR_COUNT] memory; 

    // fetch current values
    for(int i=0;i<PDATA_ADDR_COUNT;++i)
        if (storeDataAddr(fragCoord, storeAddr[i])) 
            memory[i] = fetchData(iChannel0, storeAddr[i]).xyz;

    bool resetDisplayCount = false; 
    int focus = int(fetchData(iChannel0, FOCUS_ADDR).x);

    if(alterFocusedVariable(focus,  0,memory)) resetDisplayCount = true;
    if(alterFocusedVariable(focus+1,1,memory)) resetDisplayCount = true;

    // output possibly altered values
    for(int i=0;i<PDATA_ADDR_COUNT;++i)
        if (storeDataAddr(fragCoord, storeAddr[i])) 
            fragColor.xyz = memory[i];
    // ==========================================================       

    // alter focus.  update displayCount. toggle animationEnable --------
    if (storeDataAddr(fragCoord, FOCUS_ADDR)) {
        fragColor = fetchData(iChannel0, FOCUS_ADDR);
        float focusChange = 0.;
        if(getKeyClick(KEY_UP))   focusChange = -1.;
        if(getKeyClick(KEY_DOWN)) focusChange = +1.;
        if(getKeyClick(KEY_PGUP)) focusChange = -5.;
        if(getKeyClick(KEY_PGDN)) focusChange = +5.;

        if(focusChange != 0.) {

            //skip past UNUSED rows
            while(true) {
                fragColor.x += focusChange;
                if(fragColor.x < 0.0) fragColor.x = float(UI_SIZE-1);
                if(fragColor.x >= float(UI_SIZE)) fragColor.x = 0.0;

                if(pData[int(fragColor.x)].start != UNUSED) break;
            }

            resetDisplayCount = true; 
        }

        if(resetDisplayCount) fragColor.y = iFrameRate; else
        if(--fragColor.y < 0.0) fragColor.y = 0.0;  // so widget display times out 

        bool kx = getKeyClick(88); // 'X'  animationEnable
        if(kx) fragColor.z = (fragColor.z == 0.) ? 1. : 0.;
    }   
}
