/*
Press Up/Dn arrow keys to select widget focus.
Press  Lt/Rt arrow keys to alter widget value.
Drag mouse on X axis to alter focused widget value.
Press <Spc> to reset.

Cross-eyed 3D
Slightly cross your eyes so that there is a third image in the middle.
Relax your gaze, and concentrate only on that middle image, which is in 3D.

based on https://www.shadertoy.com/view/MtfGWM
*/

struct Dataset {
    // DE() params
	vec4  c,d;

	int maxsteps;
    vec3 target;    
    bool inversion;
    vec3 invpos;
    float invradius;
    float invangle;    

    vec3 camera;
    float epsilon;
    float ambient,contrast,vibrant,dim;
    float fogDistance,fogAmount,fogColor; // distance = 0.0 -> disable effect

    vec3 lightColor;
    vec3 lightPosition;
    vec3 lightParam; // diffuse, specular, ambient

    vec4 orbitTrap;
    float Ostrength; // 0.0 -> disable orbit trapping
    float Ocycle;
    float OXwt,OXcr; // weight, color code
    float OYwt,OYcr;
    float OZwt,OZcr;
    float OWwt,OWcr;
    vec3  Ocenter;   // origin offset

    bool stereo;
    float parallax;  // adjust for best stereo effect 0 ... 0.2

    // calculated params 
    vec3 direction;
    vec3 normal;
    vec3 position;
    vec3 color;
    float depth;
    int iter;

    int focus,row,displayCount;
    vec3 viewVector,sideVector,topVector;
};

// --------------------------------------------------------
// based on https://www.shadertoy.com/view/lstyR4

#define pi 3.14159

float DE(vec3 p,inout Dataset data) {
	float k, scale=1.;

	for(int i=0;i<100;++i) {
		if(i > data.maxsteps) break;

		p = 2.*clamp(p, data.c.xyz, data.d.xyz)-p;

		k = max(data.c.w/dot(p,p), 1.);
		p *= k;
		scale *= k;

		if(i < 3) {
			vec3 ot = (p - data.Ocenter);
			data.orbitTrap = min(data.orbitTrap, vec4(abs(ot), dot(ot,ot)));
		}
	}

	float rxy = length(p.xy);
	return .927 * max(rxy - data.d.w, /*abs*/(rxy * p.z) / length(p))/scale;
}

// --------------------------------------------------------
// distance estimation function wrapped by spherical inversion code.

float DE_plusSpherical(vec3 pos,inout Dataset data) {
    if(data.inversion) {
        pos = pos - data.invpos;
        float r = length(pos);
        float r2 = r*r;
		float radius2 = data.invradius * data.invradius;
        pos = (radius2 / r2) * pos + data.invpos;

        float an = atan(pos.y,pos.x) + data.invangle;
        float ra = length(pos.xy);
        pos.x = cos(an) * ra;
        pos.y = sin(an) * ra;

        float de = DE(pos,data);
        return r2 * de / (radius2 + r * de);
    }

    return DE(pos,data);
}

// --------------------------------------------------------

vec3 generateColor(float weight,float x) {
	if(weight == 0.0) return vec3(0.0);

	x = cos(x);
	//x=abs(sin(x)); // limits colors

    // bezier, you can rearange the functions for different color combos but this one is best
	float r = (1.0-x)*(1.0-x);
	float g = x*x;
	float b = 2.0*(1.0-x)*x;
	return vec3(r,g,b);
}

vec3 orbitTrapCycle(float cycle,vec3 c, float s) {
    float ss = s * cycle;
    return vec3(0.5) + 0.5 * vec3( cos(ss + c.x), cos(ss + c.y), cos(ss + c.z));
}

vec3 getOrbitColor(inout Dataset data) {
    vec3 orbitColor;

    if (data.Ocycle > 0.0) {
        orbitColor =
        orbitTrapCycle(data.Ocycle,generateColor(data.OXwt,data.OXcr), data.orbitTrap.x) * data.OXwt * data.orbitTrap.x +
        orbitTrapCycle(data.Ocycle,generateColor(data.OYwt,data.OYcr), data.orbitTrap.y) * data.OYwt * data.orbitTrap.y +
        orbitTrapCycle(data.Ocycle,generateColor(data.OZwt,data.OZcr), data.orbitTrap.z) * data.OZwt * data.orbitTrap.z +
        orbitTrapCycle(data.Ocycle,generateColor(data.OWwt,data.OWcr), data.orbitTrap.w) * data.OWwt * data.orbitTrap.w;
    } else {
        orbitColor =
        	generateColor(data.OXwt,data.OXcr) * data.OXwt * data.orbitTrap.x +
        	generateColor(data.OYwt,data.OYcr) * data.OYwt * data.orbitTrap.y +
        	generateColor(data.OZwt,data.OZcr) * data.OZwt * data.orbitTrap.z +
        	generateColor(data.OWwt,data.OWcr) * data.OWwt * data.orbitTrap.w;
    }

    return orbitColor;
}

vec3 phong_contrib(inout Dataset data) { 
	vec3 L = normalize(data.lightPosition - data.position);
	float dotLN = dot(L, data.normal);
	if (dotLN < 0.0)
		return vec3(0, 0, 0);

	float t1 = data.lightParam.x * dotLN;

	vec3 V = normalize(data.camera - data.position);
	vec3 R = normalize(reflect(-L, data.normal));
	float dotRV = dot(R, V);

	if (dotRV < 0.0)
		return vec3(data.lightColor * t1);

	float t2 = data.lightParam.y * pow(abs(dotRV), data.lightParam.z);
	data.color += data.lightColor * (t1 + t2);
}


void applyColoring(inout Dataset data) { 
    data.color = vec3(data.ambient) + vec3(1.0 - (data.normal * data.vibrant + sqrt(float(data.iter) * data.dim)));
    data.color = vec3(0.5) + (data.color - vec3(0.5)) * data.contrast;

    if(length(data.lightColor) > 0.0) 
		phong_contrib(data);


    if(data.Ostrength > 0.0) {
        vec3 oColor = getOrbitColor(data);
        data.color = mix(data.color, oColor, data.Ostrength);
    }
}

// --------------------------------------------------------
// normal vector for 3D coordinate determined by comparing values of neighboring positions

void calcNormal(inout Dataset data) {
    float ex = data.epsilon;
    float ey = -ex;
    vec3 pos = data.position;

    vec3 t1 = vec3( pos[0] + ex, pos[1] + ey, pos[2] + ey); float a1 = DE(t1,data);
    vec3 t2 = vec3( pos[0] + ey, pos[1] + ey, pos[2] + ex); float a2 = DE(t2,data);
    vec3 t3 = vec3( pos[0] + ey, pos[1] + ex, pos[2] + ey); float a3 = DE(t3,data);
    vec3 t4 = vec3( pos[0] + ex, pos[1] + ex, pos[2] + ex); float a4 = DE(t4,data);

    data.normal = normalize(vec3(
        ex * a1 + ey * a2 + ey * a3 + ex * a4,
        ey * a1 + ey * a2 + ex * a3 + ex * a4,
        ey * a1 + ex * a2 + ey * a3 + ex * a4 ));
}

// --------------------------------------------------------

void setDirection(vec2 fragCoord,inout Dataset data) {
    vec3 viewVector = normalize(data.target - data.camera);
    vec3 sideVector = vec3(0.0, 1.0, 0.0);
    sideVector = cross(sideVector,viewVector);
    sideVector = normalize(sideVector);
    vec3 topVector = viewVector;
    topVector = cross(topVector,sideVector);

    // cross eyed stereo ------------------------------------
    vec2 srcP = fragCoord;        // copy of pixel coordinate; x is altered for stereo
    float xsize = iResolution.x;  // copy of window size; x is altered for stereo

    if(data.stereo) {
        vec3 soffset = sideVector * data.parallax;
		xsize *= 0.5;             // window x size adjusted for 2 views side by side
        if(srcP.x >= xsize) {     // right side of stereo pair?
			srcP.x -= xsize;      // base 0  X coordinate
			data.camera -= soffset;// adjust for right side parallax
		}
		else {
			data.camera += soffset;// adjust for left side parallax
		}
    }

    vec2 vPos = (srcP - iResolution.xy * 0.5)/iResolution.y;
    data.direction = normalize(viewVector + vPos.x * sideVector + vPos.y * topVector);
}

// --------------------------------------------------------
const float MIN_DIST = 0.001;
const float MAX_DIST = 30.0;

void rayMarch(inout Dataset data) {
	vec3 position = data.camera;
	float distance;
	data.iter = 0;
	data.depth = MIN_DIST;

	for(int i = 0;i < 100;++i) {
		distance = DE_plusSpherical(position,data);
        if(abs(distance) < MIN_DIST || data.depth > MAX_DIST) break;
        data.iter += 1;

        data.depth += distance;
        position = data.camera + data.direction * data.depth;
  }
}

