vec3 rowColor(inout Dataset data) {
 if(data.row == data.focus) return vec3(0.,1.,0.);
 return vec3(0.8);
}

void displayData(inout Dataset data,inout vec4 fragColor, in vec2 fragCoord) {
    if(fragCoord.x > iResolution.x * 0.33) return; // no printing in that region

    vec2 uv = fragCoord / iResolution.y;
    vec3 col = vec3(0);    

    const float font_size = 15.0; // Font Size (higher values = smaller font)
    uv *= font_size;        // Scale font with font_size
    uv.y -= font_size - 1.; // Start drawing from the top   

    for(data.row = 0; data.row < 16; ++data.row) {
       if(data.row >= data.focus-1 && data.row <= data.focus+1) {
            if(data.row ==  0) col += rowColor(data) * pcx(uv, data.c.x);
            if(data.row ==  1) col += rowColor(data) * pcy(uv, data.c.y);
            if(data.row ==  2) col += rowColor(data) * pcz(uv, data.c.z);
            if(data.row ==  3) col += rowColor(data) * pcw(uv, data.c.w);
            if(data.row ==  4) col += rowColor(data) * pdx(uv, data.d.x);
            if(data.row ==  5) col += rowColor(data) * pdy(uv, data.d.y);
            if(data.row ==  6) col += rowColor(data) * pdz(uv, data.d.z);
            if(data.row ==  7) col += rowColor(data) * pdw(uv, data.d.w);
            if(data.row ==  8) col += rowColor(data) * printInvposx(uv, data.invpos.x);
            if(data.row ==  9) col += rowColor(data) * printInvposy(uv, data.invpos.y);
            if(data.row == 10) col += rowColor(data) * printInvposz(uv, data.invpos.z);
            if(data.row == 11) col += rowColor(data) * printInvRadius(uv, data.invradius);
            if(data.row == 12) col += rowColor(data) * printInvAngle(uv, data.invangle);
            if(data.row == 13) col += rowColor(data) * printCamerax(uv, data.camera.x);
            if(data.row == 14) col += rowColor(data) * printCameray(uv, data.camera.y);
            if(data.row == 15) col += rowColor(data) * printCameraz(uv, data.camera.z);
        }

        uv.y += 0.8;  
    }

    if(length(col) > 0.)
    fragColor.xyz = col;
}   

// animation routines ----------------------------------------

void cycleValue(inout float value, float vmin,float vmax, float speed) {
    float ratio = 0.5 + 0.5 * cos(iTime * speed);  // 0 ... 1
    value = vmin + (vmax - vmin) * ratio;
}

void cycleValue2(inout float value, float baseValue, float deviation, float speed) {
    float ratio = 1.0 + cos(iTime * speed);  // 0 ... 2
    value = baseValue-deviation + deviation * ratio;
}

void cycleValue3(inout float value, float baseValue, float amount, float speed) {
    float ratio = 1.0 + cos(iTime * speed);  // 0 ... 2
    float deviation = baseValue * amount * 0.5;    
    value = baseValue-deviation + deviation * ratio;
}

void cycleValue3Vec3(inout vec3 value, vec3 baseValue, float amount, float speed) {
    for(int i=0;i<3;++i)
        cycleValue3(value[i],baseValue[i],amount,speed);
}

void moveCamera(inout Dataset data) {
    const float amt = 5.;
    cycleValue2(data.camera.x,0.0,amt, 0.035);
    cycleValue2(data.camera.y,0.0,amt, 0.045);
    cycleValue2(data.camera.z,0.0,amt, 0.055);
}

void moveTarget(inout Dataset data) {
    cycleValue2(data.target.x,0.0,1.0, 0.3);
    cycleValue2(data.target.y,0.0,1.0, 0.4);
    cycleValue2(data.target.z,0.0,1.0, 0.5);
}

void alterLight(inout Dataset data) {
   cycleValue3(data.lightColor.x,2.5,2.0,0.3);
   cycleValue3(data.lightColor.y,2.5,2.0,0.4);
   cycleValue3(data.lightColor.z,2.5,2.0,0.5);

   cycleValue(data.lightPosition.x,-2.0,2.0,0.3);
   cycleValue(data.lightPosition.y,-2.0,2.0,0.35);
   cycleValue(data.lightPosition.z,-2.0,2.0,0.4);

   cycleValue2(data.lightParam.x,0.15,0.02,0.2); // diffuse
   cycleValue2(data.lightParam.y,0.15,0.02,0.3); // specular
   cycleValue2(data.lightParam.z,0.15,0.02,0.4); // ambient
}

void alterOrbitColors(inout Dataset data) {
    const float weight = 0.6;
    cycleValue(data.OXwt,-weight,weight,0.02); // weights
    cycleValue(data.OYwt,-weight,weight,0.03);
    cycleValue(data.OZwt,-weight,weight,0.05);
 //   cycleValue(data.OWwt,-weight,weight,0.07);

    const float color = 2.0;
    cycleValue(data.OXcr,-color,color,0.01);  // color code
    cycleValue(data.OYcr,-color,color,0.02);
    cycleValue(data.OZcr,-color,color,0.03);
    cycleValue(data.OWcr,-color,color,0.05);

    cycleValue(data.Ocenter.x,-1.,1.0,0.3);
    cycleValue(data.Ocenter.y,-1.,1.0,0.5);
    cycleValue(data.Ocenter.z,-1.,1.0,0.7);
}

void animateSpecifiedVariables(inout Dataset data) {
    const float amt = 0.4;
    cycleValue2(data.c.x,data.c.x, amt,0.03);
    cycleValue2(data.c.y,data.c.y, amt,0.04);
    cycleValue2(data.c.z,data.c.z, amt,0.05);
    cycleValue2(data.c.w,data.c.w, amt,0.06);

    cycleValue2(data.d.x,data.d.x, amt,0.035);
    cycleValue2(data.d.y,data.d.y, amt,0.045);
    cycleValue2(data.d.z,data.d.z, amt,0.055);
    cycleValue2(data.d.w,data.d.w, amt,0.065);

    alterOrbitColors(data);
    alterLight(data); 

    moveCamera(data);
   // moveTarget(data);

/*
    alterLight(data); 
    moveTarget(data);

    cycleValue3(data.Ocycle,OcycleDefault,1.2,0.1);
    alterOrbitColors(data);

    cycleValue3(data.Ostrength,OstrengthDefault,1.2,0.1);
*/
}

// --------------------------------------------------------

void readStoredData(inout Dataset data) {
    vec3 sd = fetchData(iChannel0, FOCUS_ADDR).xyz;
    data.focus = int(sd.x);
    data.displayCount = int(sd.y);

    sd = fetchData(iChannel0, P0_ADDR).xyz;
    data.c.x = sd.x;
    data.c.y = sd.y;   
    data.c.z = sd.z;
    sd = fetchData(iChannel0, P1_ADDR).xyz;
    data.c.w = sd.x;
    data.d.x = sd.y;
    data.d.y = sd.z;
    sd = fetchData(iChannel0, P2_ADDR).xyz;
    data.d.z = sd.x;
    data.d.w = sd.y;
    data.invpos.x = sd.z;
    sd = fetchData(iChannel0, P3_ADDR).xyz;
    data.invpos.y = sd.x;
    data.invpos.z = sd.y;
    data.invradius = sd.z;    
    sd = fetchData(iChannel0, P4_ADDR).xyz;
    data.invangle = sd.x;
    data.camera.x = sd.y;
    data.camera.y = sd.z;
    sd = fetchData(iChannel0, P5_ADDR).xyz;
    data.camera.z = sd.x;
}

void initializedNonStoredData(inout Dataset data) {
    data.maxsteps = 7;
	data.target = targetDefault;
    data.orbitTrap = vec4(10000.);
    data.epsilon = 0.00001; // deviation when calculating normal

    data.ambient = 0.56;
    data.contrast = 0.7;
    data.vibrant = 0.0999;
    data.dim = 0.035;
	data.color = vec3(0,0,0); 

    data.fogDistance = 0.0 ; // set to 0.0 to disable fog
    data.fogAmount = 0.02;
    data.fogColor = 0.5;

    data.lightColor = vec3(0.5,0.5,0.5); // set to 0.0 to disable light #1
    data.lightPosition = vec3(9.0,17.0,-20.0);
    data.lightParam = vec3(0.1,0.3,10.13);

    data.inversion = false; 

    data.Ostrength = 0.063; //OstrengthDefault;
    data.Ocycle = 0.0; // OcycleDefault;
    data.OXwt = OXwtDefault;
    data.OXcr = OXcrDefault;
    data.OYwt = OYwtDefault;
    data.OYcr = OYcrDefault;
    data.OZwt = OZwtDefault;
    data.OZcr = OZcrDefault;
    data.OWwt = OWwtDefault;
    data.OWcr = OWcrDefault;
    data.Ocenter = OcenterDefault;

    data.stereo = false;
    data.parallax = 0.01; // use smaller values when viewing closeup objects
}

// --------------------------------------------------------

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    Dataset data;
    readStoredData(data);
    initializedNonStoredData(data);
    animateSpecifiedVariables(data);

    setDirection(fragCoord,data);
	rayMarch(data);

	if(data.depth < MAX_DIST) {  
        data.position = data.camera + data.direction * data.depth;
        calcNormal(data);
		applyColoring(data);
    }

    if(data.fogDistance != 0.0) {
        float f = data.depth - data.fogDistance;
        if(f > 0.0) {
            f = min(1.0,f * data.fogAmount);
            data.color = mix(data.color, vec3(0.2 + f * data.fogColor),f);
        }
    }

    fragColor = vec4(data.color,1.0);

    if(data.displayCount > 0) 
        displayData(data,fragColor,fragCoord);
}

